import{_ as t,o as e,c as o,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 58.1-2674.1 Application for correction of certification to Department of Taxation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-2674.1.md","filePath":"vacode/58.1-2674.1.md"}'),a={name:"vacode/58.1-2674.1.md"},r=i('<h1 id="§-58-1-2674-1-application-for-correction-of-certification-to-department-of-taxation" tabindex="-1">§ 58.1-2674.1 Application for correction of certification to Department of Taxation. <a class="header-anchor" href="#§-58-1-2674-1-application-for-correction-of-certification-to-department-of-taxation" aria-label="Permalink to &quot;§ 58.1-2674.1 Application for correction of certification to Department of Taxation.&quot;">​</a></h1><p>Any telecommunications company or electric supplier aggrieved by any action of the Commission in the certification of gross receipts to the Department of Taxation as required by § <a href="http://law.lis.virginia.gov/vacode/58.1-400.1/" target="_blank" rel="noreferrer">58.1-400.1</a> or § <a href="http://law.lis.virginia.gov/vacode/58.1-400.3/" target="_blank" rel="noreferrer">58.1-400.3</a> may apply to the Commission for review and correction of any specified item or items of a certification. Such application shall be in a form prescribed by the Commission and shall be filed within 18 months of the date of the certification to the Department of Taxation after which date the Commission shall have no authority under this section or any other provision of law to receive any application or complaint concerning the certification. The Commission shall provide for notice to the Department of Taxation of any application. If, from the evidence introduced at any hearing on the application or its own investigation, the Commission finds that the certification is incorrect, it shall correct the certification to the Department of Taxation.</p><p>2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0368" target="_blank" rel="noreferrer">368</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0716" target="_blank" rel="noreferrer">716</a>.</p>',3),n=[r];function c(f,s,p,l,h,m){return e(),o("div",null,n)}const g=t(a,[["render",c]]);export{_ as __pageData,g as default};
