import{_ as e,o,c as a,O as r}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 58.1-2690 No state or local tax on intangible personal property or money; local levies and license taxes.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-2690.md","filePath":"vacode/58.1-2690.md"}'),t={name:"vacode/58.1-2690.md"},n=r('<h1 id="§-58-1-2690-no-state-or-local-tax-on-intangible-personal-property-or-money-local-levies-and-license-taxes" tabindex="-1">§ 58.1-2690 No state or local tax on intangible personal property or money; local levies and license taxes. <a class="header-anchor" href="#§-58-1-2690-no-state-or-local-tax-on-intangible-personal-property-or-money-local-levies-and-license-taxes" aria-label="Permalink to &quot;§ 58.1-2690 No state or local tax on intangible personal property or money; local levies and license taxes.&quot;">​</a></h1><p>A. Except as provided in this chapter, there shall be no state or local taxes assessed on the intangible personal property, gross receipts or other such money or income owned by telephone or telegraph companies, railroads, pipeline companies, or corporations furnishing water, heat, light and power by means of electricity, gas or steam.</p><p>B. On the real estate and tangible personal property of every incorporated telegraph and telephone company owning or operating telegraph or telephone lines in Virginia and of railroads, pipeline companies, or corporations furnishing water, heat, light and power by means of electricity, gas or steam, there shall be local levies at the rates prescribed by § <a href="http://law.lis.virginia.gov/vacode/58.1-2606/" target="_blank" rel="noreferrer">58.1-2606</a>.</p><p>C. Notwithstanding the provisions of subsection A, any county, city or town may impose a license tax under § <a href="http://law.lis.virginia.gov/vacode/58.1-3703/" target="_blank" rel="noreferrer">58.1-3703</a> upon a corporation owning or operating telegraph or telephone lines in Virginia for the privilege of doing business therein, which shall not exceed one-half of one percent of the gross receipts of such business accruing to such corporation from such business in such county, city or town; however, charges for long distance telephone calls shall not be considered receipts of business in such county, city or town.</p><p>D. Notwithstanding the provisions of subsection A, any county, city or town may impose an excise tax under § <a href="http://law.lis.virginia.gov/vacode/58.1-3818.3/" target="_blank" rel="noreferrer">58.1-3818.3</a> upon a corporation owning or operating telegraph or telephone lines in Virginia, at a rate that shall not exceed the rate lawfully imposed by § <a href="http://law.lis.virginia.gov/vacode/58.1-3818.3/" target="_blank" rel="noreferrer">58.1-3818.3</a>, on such corporation&#39;s gross receipts from sales of video programming or access to video programming directly to end-user subscribers who are located within such county, city or town.</p><p>Code 1950, §§ 58-518, 58-523, 58-578, 58-593, 58-596, 58-602, 58-606; 1968, c. 637; 1972, cc. 813, 858; 1978, c. 784; 1984, c. 675; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0751" target="_blank" rel="noreferrer">751</a>.</p>',6),i=[n];function s(l,c,p,h,g,d){return o(),a("div",null,i)}const u=e(t,[["render",s]]);export{y as __pageData,u as default};
