import{_ as t,o,c as n,z as e,a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 58.1-3001 When boards of supervisors to fix and order county and district taxes; funds not available, allocated, etc., until appropriated.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-3001.md","filePath":"vacode/58.1-3001.md"}'),r={name:"vacode/58.1-3001.md"},s=e("h1",{id:"§-58-1-3001-when-boards-of-supervisors-to-fix-and-order-county-and-district-taxes-funds-not-available-allocated-etc-until-appropriated",tabindex:"-1"},[a("§ 58.1-3001 When boards of supervisors to fix and order county and district taxes; funds not available, allocated, etc., until appropriated. "),e("a",{class:"header-anchor",href:"#§-58-1-3001-when-boards-of-supervisors-to-fix-and-order-county-and-district-taxes-funds-not-available-allocated-etc-until-appropriated","aria-label":'Permalink to "§ 58.1-3001 When boards of supervisors to fix and order county and district taxes; funds not available, allocated, etc., until appropriated."'},"​")],-1),i=e("p",null,"The governing body of each county shall, at its regular meeting in the month of January in each year, or as soon thereafter as practicable not later than a regular or called meeting in June, fix the amount of the county and district taxes for the current year. Any such governing body may provide that if any taxpayer owns tangible personal property of such small value that the local levies thereon for the year result in a tax of less than fifteen dollars, such tax may be collected as provided by ordinance or such property may be omitted from the personal property book and no assessment made thereon.",-1),d=e("p",null,"The imposition of taxes or the collection of such taxes shall not constitute an appropriation nor an obligation or duty to appropriate any funds for any purpose, expenditure or contemplated expenditure. No part of the funds raised by the general county taxes shall be considered available, allocated or expended for any purpose until there has been an appropriation of funds for that expenditure or purpose by the governing body either annually, semiannually, quarterly, or monthly. There shall be no mandatory duty upon the governing body of any county to appropriate any funds raised by general county taxes except to pay the principal and interest on bonds and other legal obligations of the county or district and to pay obligations of the county or its agencies and departments arising under contracts executed or approved by the governing body, unless otherwise specifically provided by statute. Any funds collected and not expended in any fiscal year shall be carried over to the succeeding fiscal years and shall be available for appropriation for any governmental purposes in those years.",-1),l=e("p",null,[a("Code 1950, § 58-839; 1950, p. 416; 1952, c. 423; 1958, c. 35; 1959, Ex. Sess., c. 52; 1984, c. 675; 1988, c. 430; 1989, c. 81; 1994, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0252",target:"_blank",rel:"noreferrer"},"252"),a(".")],-1),c=[s,i,d,l];function p(u,h,f,y,b,x){return o(),n("div",null,c)}const m=t(r,[["render",p]]);export{g as __pageData,m as default};
