import{_ as t,o as a,c as r,z as e,a as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 58.1-3003 Appeal from order of levy.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-3003.md","filePath":"vacode/58.1-3003.md"}'),n={name:"vacode/58.1-3003.md"},s=e("h1",{id:"§-58-1-3003-appeal-from-order-of-levy",tabindex:"-1"},[o("§ 58.1-3003 Appeal from order of levy. "),e("a",{class:"header-anchor",href:"#§-58-1-3003-appeal-from-order-of-levy","aria-label":'Permalink to "§ 58.1-3003 Appeal from order of levy."'},"​")],-1),i=e("p",null,[o("If the attorney for the Commonwealth of any county is of the opinion that an order for imposition of taxes made by his governing body is illegal or if he receives a petition of one per centum of the registered voters of the county, but no fewer than fifty such voters, demanding that he appeal such order, such attorney shall appeal therefrom, within thirty days after such order is made, to the circuit court of the county and such appeal shall operate as a supersedeas. Without waiting the final decision of the appeal the governing body may rescind its order and impose taxes according to law. If the court, on the hearing of the appeal, is of the opinion that the order is contrary to law, it shall reverse the same and direct the governing body to enter such order as to the court may seem right. If money be collected under any such order, which is afterward rescinded or reversed, the treasurer shall forthwith repay such money to the person from whom it was collected. If he fails so to do, a motion may be made and judgment obtained, in like manner as in cases provided in § "),e("a",{href:"http://law.lis.virginia.gov/vacode/58.1-3140/",target:"_blank",rel:"noreferrer"},"58.1-3140"),o(".")],-1),d=e("p",null,"Code 1950, § 58-841; 1980, c. 501; 1984, c. 675.",-1),h=[s,i,d];function c(l,f,p,m,u,y){return a(),r("div",null,h)}const g=t(n,[["render",c]]);export{_ as __pageData,g as default};
