import{_ as o,o as s,c as a,z as e,a as t}from"./chunks/framework.9482e208.js";const x=JSON.parse('{"title":"§ 58.1-3014 Relief from taxes in cases of disaster.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-3014.md","filePath":"vacode/58.1-3014.md"}'),r={name:"vacode/58.1-3014.md"},n=e("h1",{id:"§-58-1-3014-relief-from-taxes-in-cases-of-disaster",tabindex:"-1"},[t("§ 58.1-3014 Relief from taxes in cases of disaster. "),e("a",{class:"header-anchor",href:"#§-58-1-3014-relief-from-taxes-in-cases-of-disaster","aria-label":'Permalink to "§ 58.1-3014 Relief from taxes in cases of disaster."'},"​")],-1),i=e("p",null,"All taxpayers of the Commonwealth whose lands, improvements thereon, or personal property, or any portion thereof, shall be in any year destroyed in any manner by common disaster, if declared such by the Governor of Virginia, may on application therefor be relieved from the payment of taxes and levies to any county, city or town upon such land, improvements thereon, or personal property as shall be so taken and which are uncompensated for by insurance or otherwise. Such relief shall be for that year in which such property is taken from and after the date upon which such disaster occurred, if so provided by resolution adopted by the governing body of such locality. Any such taxpayer who has not paid the taxes or levies on any such land, improvements thereon, or personal property so taken shall also be relieved of interest and penalties therefor; provided, he shall make payment for his proportion, if any, of the taxes and levies for the year during which the land, improvements thereon, or personal property was so taken, on or before July 1 of the year following.",-1),h=e("p",null,"Any taxpayer entitled to such relief may apply within one year of such disaster to the commissioner of the revenue or other assessing officer of such locality, who shall determine the amount by which the assessment on such property should be reduced by reason of such loss. If such tax has not been paid, the assessing officer shall exonerate the applicant from the payment of so much of the tax as is allocable to such loss. If such tax has been paid, the assessing officer shall certify the amount of such reduction to the treasurer of such locality, who shall issue a refund therefor.",-1),l=e("p",null,"Code 1950, § 58-27.2; 1970, c. 762; 1973, c. 140; 1978, c. 654; 1984, c. 675.",-1),c=[n,i,h,l];function d(f,p,m,u,y,_){return s(),a("div",null,c)}const v=o(r,[["render",d]]);export{x as __pageData,v as default};
