import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 58.1-3015 To whom property generally shall be taxed and by whom listed.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-3015.md","filePath":"vacode/58.1-3015.md"}'),r={name:"vacode/58.1-3015.md"},s=o('<h1 id="§-58-1-3015-to-whom-property-generally-shall-be-taxed-and-by-whom-listed" tabindex="-1">§ 58.1-3015 To whom property generally shall be taxed and by whom listed. <a class="header-anchor" href="#§-58-1-3015-to-whom-property-generally-shall-be-taxed-and-by-whom-listed" aria-label="Permalink to &quot;§ 58.1-3015 To whom property generally shall be taxed and by whom listed.&quot;">​</a></h1><p>If property is owned by a person sui juris, it shall be taxed to him.</p><p>If property is owned by a minor, it shall be listed by and taxed to his guardian or trustee, if any he has; if he has no guardian or trustee, it shall be listed by and taxed to the person in possession.</p><p>If the property is the estate of a deceased person, it shall be listed by the personal representative or person in possession and taxed to the estate of such deceased person.</p><p>If the property is owned by an incapacitated person as that term is defined in § <a href="http://law.lis.virginia.gov/vacode/64.2-2000/" target="_blank" rel="noreferrer">64.2-2000</a>, it shall be listed by and taxed to his conservator or guardian, if any; if none has been appointed, then such property shall be listed by and taxed to the person in possession.</p><p>If the property is held in trust for the benefit of another, it shall be listed by and taxed to the trustee, if there is any in this Commonwealth, and if there is no trustee in this Commonwealth, it shall be listed by and taxed to the beneficiary.</p><p>If the property belongs to a corporation or firm, it shall be listed by and taxed to the corporation or firm.</p><p>Code 1950, § 58-20; 1972, c. 825; 1984, c. 675; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0921" target="_blank" rel="noreferrer">921</a>.</p>',8),i=[s];function n(l,d,h,p,b,c){return t(),a("div",null,i)}const _=e(r,[["render",n]]);export{f as __pageData,_ as default};
