import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 58.1-3131 Warrants; recordkeeping requirements; release of information.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-3131.md","filePath":"vacode/58.1-3131.md"}'),n={name:"vacode/58.1-3131.md"},i=t('<h1 id="§-58-1-3131-warrants-recordkeeping-requirements-release-of-information" tabindex="-1">§ 58.1-3131 Warrants; recordkeeping requirements; release of information. <a class="header-anchor" href="#§-58-1-3131-warrants-recordkeeping-requirements-release-of-information" aria-label="Permalink to &quot;§ 58.1-3131 Warrants; recordkeeping requirements; release of information.&quot;">​</a></h1><p>The treasurer shall maintain a record in which he shall make an entry of all warrants and other legal demand instruments legally drawn upon him by the governing body and presented for payment, stating correctly the amount, number, in whose favor drawn and the date such warrant was issued. All such warrants and other legal demand instruments shall be paid, in the order presented, out of the fund drawn upon.</p><p>No information contained in the record of warrants and other legal demand instruments, including any invoice that has been presented to a locality for payment, and the locality has attempted to pay it, but the payment has not been completed because electronic payment has failed or a check was mailed but not cashed, shall be released for any purpose except (i) that the local governing body may publish aggregated information relating to warrants and other legal demand instruments paid, as classified by expenditure item, recipient, date, or disbursement, or (ii) as a means of establishing the status of a claim previously reported as having been paid when a person legally entitled to the funds presents evidence that a previously submitted claim has not been paid. In no case, however, shall the governing body of any county, city, or town publish any information that is prohibited from release under federal or state law, including but not limited to confidential records held pursuant to § <a href="/vacode/58.1-3/">58.1-3</a>.</p><p>Code 1950, § 58-920; 1984, c. 675; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0931" target="_blank" rel="noreferrer">931</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0485" target="_blank" rel="noreferrer">485</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0597" target="_blank" rel="noreferrer">597</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0088" target="_blank" rel="noreferrer">88</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0031" target="_blank" rel="noreferrer">31</a>.</p>',4),o=[i];function s(l,d,c,h,p,f){return a(),r("div",null,o)}const u=e(n,[["render",s]]);export{m as __pageData,u as default};
