import{_ as t,o as r,c as o,z as e,a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 58.1-3171 Attorney General to proceed against delinquent treasurers and their sureties; recordation of notice.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-3171.md","filePath":"vacode/58.1-3171.md"}'),n={name:"vacode/58.1-3171.md"},s=e("h1",{id:"§-58-1-3171-attorney-general-to-proceed-against-delinquent-treasurers-and-their-sureties-recordation-of-notice",tabindex:"-1"},[a("§ 58.1-3171 Attorney General to proceed against delinquent treasurers and their sureties; recordation of notice. "),e("a",{class:"header-anchor",href:"#§-58-1-3171-attorney-general-to-proceed-against-delinquent-treasurers-and-their-sureties-recordation-of-notice","aria-label":'Permalink to "§ 58.1-3171 Attorney General to proceed against delinquent treasurers and their sureties; recordation of notice."'},"​")],-1),i=e("p",null,"The Attorney General shall proceed against all treasurers who are in default and against their sureties for the recovery of the amounts due from such treasurers, respectively, upon receiving from the Comptroller information of such default. The proceedings may be by motion in the appropriate circuit court. Copies of such motion, certified by the clerk of the court, shall be forthwith sent by the Attorney General to any of the clerks of the circuit courts of the counties and cities wherein it is ascertained that such treasurer or his sureties have any estate. The clerk to whom any such copy is so sent shall record it in the same manner required by law for recordation of a deed and index the copy in the name of the Commonwealth.",-1),c=e("p",null,"Code 1950, § 58-976; 1984, c. 675.",-1),d=[s,i,c];function h(l,u,f,p,m,_){return r(),o("div",null,d)}const v=t(n,[["render",h]]);export{g as __pageData,v as default};
