import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 58.1-3221.1 Classification of land and improvements for tax purposes.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-3221.1.md","filePath":"vacode/58.1-3221.1.md"}'),i={name:"vacode/58.1-3221.1.md"},n=o('<h1 id="§-58-1-3221-1-classification-of-land-and-improvements-for-tax-purposes" tabindex="-1">§ 58.1-3221.1 Classification of land and improvements for tax purposes. <a class="header-anchor" href="#§-58-1-3221-1-classification-of-land-and-improvements-for-tax-purposes" aria-label="Permalink to &quot;§ 58.1-3221.1 Classification of land and improvements for tax purposes.&quot;">​</a></h1><p>A. In the Cities of Fairfax, Poquoson, Richmond, and Roanoke, improvements to real property are declared to be a separate class of property and shall constitute a separate classification for local taxation of real property.</p><p>B. The governing body of the City of Fairfax, the City of Richmond, and the City of Roanoke, after giving public notice and an opportunity for the public to be heard in the manner provided in § <a href="/vacode/58.1-3007/">58.1-3007</a>, may levy a tax on the property enumerated in subsection A at a different rate than the tax imposed upon the land on which it is located, provided that the rate of tax on the property described in subsection A shall not be zero and shall not exceed the rate of tax on the land on which it is located.</p><p>C. Nothing in this section shall be construed to permit the City of Fairfax, Poquoson, Richmond, or Roanoke to alter in any way its valuation of real property covered by this section.</p><p>D. The governing body of the City of Poquoson, after giving public notice and an opportunity for the public to be heard in the manner provided in § <a href="/vacode/58.1-3007/">58.1-3007</a>, may levy a tax on the property enumerated in subsection A at a different rate than the tax imposed upon the land on which it is located, provided that the rate of tax on the property described in subsection A shall not be zero.</p><p>2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0016" target="_blank" rel="noreferrer">16</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0164" target="_blank" rel="noreferrer">164</a>; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0146" target="_blank" rel="noreferrer">146</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0790" target="_blank" rel="noreferrer">790</a>.</p>',6),r=[n];function s(p,l,c,d,h,f){return t(),a("div",null,r)}const _=e(i,[["render",s]]);export{m as __pageData,_ as default};
