import{_ as t,o,c as a,z as e,a as n}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 58.1-3225 Apportionment of taxes, etc., on partition.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-3225.md","filePath":"vacode/58.1-3225.md"}'),r={name:"vacode/58.1-3225.md"},s=e("h1",{id:"§-58-1-3225-apportionment-of-taxes-etc-on-partition",tabindex:"-1"},[n("§ 58.1-3225 Apportionment of taxes, etc., on partition. "),e("a",{class:"header-anchor",href:"#§-58-1-3225-apportionment-of-taxes-etc-on-partition","aria-label":'Permalink to "§ 58.1-3225 Apportionment of taxes, etc., on partition."'},"​")],-1),i=e("p",null,"When there is a partition of any real estate owned by two or more persons as joint tenants, tenants in common or coparceners and taxes or taxes, penalty and interest or levies or assessments of any kind, whether state, county, city or town, are charged or chargeable against the joint estate, the circuit court of the county or the city in which such real estate is situated, shall, on the motion of any person to whom a portion of such real estate has been set off or allotted, or on the motion of any person who has the right to charge such portion or portions with a debt, ascertain and fix the pro rata of such amount aforesaid, which should be paid by such person on the portion or portions of such real estate set off or allotted to him. When the pro rata of such amount has been so ascertained and paid, he shall hold the portion or portions of such real estate set off or allotted to him or them, free from the residue of the tax, or tax, penalty and interest or levy or assessment charged on the tract before partition. And the portion or portions of such real estate set off or allotted to the person who shall not have paid their pro rata of the tax, or the tax, penalty and interest or levy or assessment, shall be charged with and held bound for the portion of such amount aforesaid remaining unpaid, in the same manner as if the partition had been made before the tax, or tax penalty and interest or levy or assessment had been assessed or accrued.",-1),h=e("p",null,"Code 1950, § 58-826; 1984, c. 675.",-1),c=[s,i,h];function d(p,l,f,m,u,_){return o(),a("div",null,c)}const b=t(r,[["render",d]]);export{y as __pageData,b as default};
