import{_ as e,o as r,c as o,O as n}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 58.1-3245.12 Local enterprise zone program for technology, defense, or green development zones.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-3245.12.md","filePath":"vacode/58.1-3245.12.md"}'),a={name:"vacode/58.1-3245.12.md"},t=n('<h1 id="§-58-1-3245-12-local-enterprise-zone-program-for-technology-defense-or-green-development-zones" tabindex="-1">§ 58.1-3245.12 Local enterprise zone program for technology, defense, or green development zones. <a class="header-anchor" href="#§-58-1-3245-12-local-enterprise-zone-program-for-technology-defense-or-green-development-zones" aria-label="Permalink to &quot;§ 58.1-3245.12 Local enterprise zone program for technology, defense, or green development zones.&quot;">​</a></h1><p>The governing body of any county, city, or town may also adopt a local enterprise zone development taxation program for a technology zone, as described in § <a href="http://law.lis.virginia.gov/vacode/58.1-3850/" target="_blank" rel="noreferrer">58.1-3850</a>, a defense production and support services zone, as described in § <a href="http://law.lis.virginia.gov/vacode/58.1-3853/" target="_blank" rel="noreferrer">58.1-3853</a>, or a green development zone, as described in § <a href="http://law.lis.virginia.gov/vacode/58.1-3854/" target="_blank" rel="noreferrer">58.1-3854</a>, located within its boundaries, regardless of whether such technology zone, defense production and support services zone, or green development zone has been designated by the Governor as an enterprise zone pursuant to Chapter 49 (§ <a href="http://law.lis.virginia.gov/vacode/59.1-538/" target="_blank" rel="noreferrer">59.1-538</a> et seq.) of Title 59.1. Such program for a technology zone, defense production and support services zone, or green development zone shall be adopted by local ordinance. All other provisions in this article as they relate to a local enterprise zone development taxation program for enterprise zones shall apply to such program for technology, defense production and support services, or green development zone.</p><p>2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0449" target="_blank" rel="noreferrer">449</a>; 2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0863" target="_blank" rel="noreferrer">863</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0884" target="_blank" rel="noreferrer">884</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0875" target="_blank" rel="noreferrer">875</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0877" target="_blank" rel="noreferrer">877</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0091" target="_blank" rel="noreferrer">91</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0027" target="_blank" rel="noreferrer">27</a>.</p>',3),i=[t];function l(s,p,g,c,d,f){return r(),o("div",null,i)}const _=e(a,[["render",l]]);export{v as __pageData,_ as default};
