import{_ as e,o as s,c as a,O as r}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 58.1-3258.1 Certification of supervisors, assessors and appraisers contracted by a locality to perform assessments.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-3258.1.md","filePath":"vacode/58.1-3258.1.md"}'),t={name:"vacode/58.1-3258.1.md"},o=r('<h1 id="§-58-1-3258-1-certification-of-supervisors-assessors-and-appraisers-contracted-by-a-locality-to-perform-assessments" tabindex="-1">§ 58.1-3258.1 Certification of supervisors, assessors and appraisers contracted by a locality to perform assessments. <a class="header-anchor" href="#§-58-1-3258-1-certification-of-supervisors-assessors-and-appraisers-contracted-by-a-locality-to-perform-assessments" aria-label="Permalink to &quot;§ 58.1-3258.1 Certification of supervisors, assessors and appraisers contracted by a locality to perform assessments.&quot;">​</a></h1><p>A. No supervisor, assessor or appraiser shall contract or offer to contract to perform the assessment or reassessment of real property for any locality unless he holds a valid certification issued by the Department.</p><p>B. The Department shall establish requirements for the certification of all supervisors, appraisers and personnel contracted by a locality to perform the assessment or reassessment of real property located in the locality. Such requirements shall prescribe qualifications for certification including (i) minimum education and training requirements, to include guidance for conducting appraisals of certain multi-unit real estate under § <a href="http://law.lis.virginia.gov/vacode/58.1-3295/" target="_blank" rel="noreferrer">58.1-3295</a> and guidance for following generally accepted appraisal practices; (ii) minimum levels of experience; and (iii) standards of conduct. All supervisors, appraisers, and personnel employed or contracted to perform general assessments shall be required to hold a valid certification issued by the Department.</p><p>C. The Department may establish requirements for continuing education as a prerequisite to renewal of any certificate issued under this section.</p><p>2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0540" target="_blank" rel="noreferrer">540</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0552" target="_blank" rel="noreferrer">552</a>.</p>',5),i=[o];function n(c,l,p,d,f,m){return s(),a("div",null,i)}const _=e(t,[["render",n]]);export{h as __pageData,_ as default};
