import{_ as e,o as t,c as r,O as a}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 58.1-3284.2 Reassessment of residential property containing defective drywall.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-3284.2.md","filePath":"vacode/58.1-3284.2.md"}'),i={name:"vacode/58.1-3284.2.md"},o=a('<h1 id="§-58-1-3284-2-reassessment-of-residential-property-containing-defective-drywall" tabindex="-1">§ 58.1-3284.2 Reassessment of residential property containing defective drywall. <a class="header-anchor" href="#§-58-1-3284-2-reassessment-of-residential-property-containing-defective-drywall" aria-label="Permalink to &quot;§ 58.1-3284.2 Reassessment of residential property containing defective drywall.&quot;">​</a></h1><p>A. As used in this section, &quot;defective drywall&quot; means the same as that term is defined in § <a href="http://law.lis.virginia.gov/vacode/36-156.1/" target="_blank" rel="noreferrer">36-156.1</a>.</p><p>B. An owner of residential property containing defective drywall may request the commissioner of the revenue or other assessing official where the property is located to reassess the property. After confirmation by the local building official of the presence of defective drywall in accordance with subsection C, the commissioner of the revenue or other assessing official shall (i) determine the amount by which the defective drywall has reduced the assessed value of the property, (ii) provide written notice to the owner of the reduction in value, and (iii) reassess the value of the property accordingly.</p><p>C. The local building official shall confirm the presence of defective drywall only after a review of the test results submitted to him from a testing agency that is approved by the building official and procured by the owner of the residential property.</p><p>D. The local governing body may, by ordinance, designate the residential property containing defective drywall as a rehabilitation district for purposes of granting the owner a partial real estate tax exemption pursuant to § <a href="http://law.lis.virginia.gov/vacode/58.1-3219.4/" target="_blank" rel="noreferrer">58.1-3219.4</a>.</p><p>2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0034" target="_blank" rel="noreferrer">34</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0046" target="_blank" rel="noreferrer">46</a>.</p>',6),n=[o];function s(l,c,d,f,p,h){return t(),r("div",null,n)}const y=e(i,[["render",s]]);export{v as __pageData,y as default};
