import{_ as e,o as t,c as a,O as s}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 58.1-3295.3 Assessment of real property; data centers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-3295.3.md","filePath":"vacode/58.1-3295.3.md"}'),r={name:"vacode/58.1-3295.3.md"},n=s('<h1 id="§-58-1-3295-3-assessment-of-real-property-data-centers" tabindex="-1">§ 58.1-3295.3 Assessment of real property; data centers. <a class="header-anchor" href="#§-58-1-3295-3-assessment-of-real-property-data-centers" aria-label="Permalink to &quot;§ 58.1-3295.3 Assessment of real property; data centers.&quot;">​</a></h1><p>A. As used in this section:</p><p>&quot;Computer equipment and peripherals&quot; means computer equipment and peripherals subject to classification under the provisions of subdivision A 17 of § <a href="/vacode/58.1-3503/">58.1-3503</a> or under the provisions of subdivision A 43 of § <a href="/vacode/58.1-3506/">58.1-3506</a>.</p><p>&quot;Cost approach&quot; means assessing value by determining the cost to construct a reproduction or suitable replacement of fixtures and deducting physical, functional, and economic depreciation sustained by such fixtures.</p><p>&quot;Data center&quot; means the same as such term is defined in subdivision A 43 of § <a href="/vacode/58.1-3506/">58.1-3506</a>.</p><p>&quot;Fixtures&quot; means all fixtures and equipment used in a data center except computer equipment and peripherals, equipment used for external surveillance and security, and fire and burglar alarm systems. &quot;Fixtures&quot; includes generators, radiators, exhaust fans, and fuel storage tanks; electrical substations, power distribution equipment, cogeneration equipment, and batteries; chillers, computer room air conditioners, and cool towers; heating, ventilating, and air conditioning systems; water storage tanks, water pumps, and piping; monitoring systems; and transmission and distribution equipment.</p><p>B. If fixtures are installed at a data center and taxed under the provisions of this chapter, such fixtures shall be assessed using the cost approach.</p><p>2022, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0671" target="_blank" rel="noreferrer">671</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0672" target="_blank" rel="noreferrer">672</a>.</p>',8),i=[n];function o(c,p,d,u,l,m){return t(),a("div",null,i)}const _=e(r,[["render",o]]);export{h as __pageData,_ as default};
