import{_ as t,o as a,c as s,z as e,a as o}from"./chunks/framework.9482e208.js";const b=JSON.parse(`{"title":"§ 58.1-3342 Assessment upon owner's death; liability of personalty for tax.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-3342.md","filePath":"vacode/58.1-3342.md"}`),n={name:"vacode/58.1-3342.md"},r=e("h1",{id:"§-58-1-3342-assessment-upon-owner-s-death-liability-of-personalty-for-tax",tabindex:"-1"},[o("§ 58.1-3342 Assessment upon owner's death; liability of personalty for tax. "),e("a",{class:"header-anchor",href:"#§-58-1-3342-assessment-upon-owner-s-death-liability-of-personalty-for-tax","aria-label":`Permalink to "§ 58.1-3342 Assessment upon owner's death; liability of personalty for tax."`},"​")],-1),h=e("p",null,"When an owner dies intestate, the commissioner of the revenue may ascertain who are the heirs of the intestate and charge the land to such heirs or he may charge the land to the decedent's estate until a transfer thereof. When the owner has devised the land, the commissioner may charge the same to such person as may be beneficially entitled thereto under the will. If, under the will, the land is to be sold, it shall continue charged to the decedent's estate until a transfer thereof and, while it continues so charged to the estate, the personal property shall be liable for the tax on all property so charged and subject to distress or other lawful process for the recovery of the same. Any assets in the hands of the personal representatives of the decedent shall be likewise liable therefor.",-1),i=e("p",null,"Code 1950, § 58-771; 1984, c. 675.",-1),l=[r,h,i];function d(c,f,p,m,u,_){return a(),s("div",null,l)}const w=t(n,[["render",d]]);export{b as __pageData,w as default};
