import{_ as e,o as a,c as r,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 58.1-3381 Action of board; notice required before increase made.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-3381.md","filePath":"vacode/58.1-3381.md"}'),t={name:"vacode/58.1-3381.md"},n=o('<h1 id="§-58-1-3381-action-of-board-notice-required-before-increase-made" tabindex="-1">§ 58.1-3381 Action of board; notice required before increase made. <a class="header-anchor" href="#§-58-1-3381-action-of-board-notice-required-before-increase-made" aria-label="Permalink to &quot;§ 58.1-3381 Action of board; notice required before increase made.&quot;">​</a></h1><p>A. The board shall hear and determine any and all such petitions and, by order, may increase, decrease or affirm the assessment of which complaint is made; and, by order, it may increase or decrease any assessment, upon its own motion. No assessment shall be increased until after the owner of the property has been notified and given an opportunity to show cause against such increase. In addition, no assessment shall be increased on commercial, multi-family residential, or industrial property unless such increase is recommended by the assessor in compliance with the provisions of § <a href="http://law.lis.virginia.gov/vacode/58.1-3379/" target="_blank" rel="noreferrer">58.1-3379</a>.</p><p>B. Any determination of the assessment by the board shall be deemed presumptively correct for the succeeding two years unless the assessor can demonstrate by clear and convincing evidence that a substantial change in value of the property has occurred. This subsection shall apply to the City of Virginia Beach.</p><p>Code 1950, § 58-906; 1984, c. 675; 1993, c. 136; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0813" target="_blank" rel="noreferrer">813</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0197" target="_blank" rel="noreferrer">197</a>.</p>',4),i=[n];function s(c,d,l,h,f,p){return a(),r("div",null,i)}const u=e(t,[["render",s]]);export{_ as __pageData,u as default};
