import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 58.1-339.6 Political candidate contribution tax credit.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-339.6.md","filePath":"vacode/58.1-339.6.md"}'),r={name:"vacode/58.1-339.6.md"},n=i('<h1 id="§-58-1-339-6-political-candidate-contribution-tax-credit" tabindex="-1">§ 58.1-339.6 Political candidate contribution tax credit. <a class="header-anchor" href="#§-58-1-339-6-political-candidate-contribution-tax-credit" aria-label="Permalink to &quot;§ 58.1-339.6 Political candidate contribution tax credit.&quot;">​</a></h1><p>For taxable years beginning on and after January 1, 2000, but before January 1, 2017, any individual shall be entitled to a credit against the tax levied pursuant to § <a href="http://law.lis.virginia.gov/vacode/58.1-320/" target="_blank" rel="noreferrer">58.1-320</a> of an amount equal to 50 percent of the amount contributed by the taxpayer to a candidate, as defined in § <a href="http://law.lis.virginia.gov/vacode/24.2-101/" target="_blank" rel="noreferrer">24.2-101</a>, in one or more primary, special, or general elections for local or state office held in the Commonwealth in the taxable year in which the contributions are made. The amount of the credit shall not exceed $25 for an individual taxpayer or $50 for taxpayers filing a joint return.</p><p>1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0464" target="_blank" rel="noreferrer">464</a>; 2016, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0050" target="_blank" rel="noreferrer">50</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0348" target="_blank" rel="noreferrer">348</a>.</p>',3),o=[n];function l(c,d,s,h,_,f){return a(),t("div",null,o)}const g=e(r,[["render",l]]);export{u as __pageData,g as default};
