import{_ as a,o as e,c as t,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 58.1-3508.1 Separate classification of machinery and tools used in semiconductor manufacturing.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-3508.1.md","filePath":"vacode/58.1-3508.1.md"}'),n={name:"vacode/58.1-3508.1.md"},i=o('<h1 id="§-58-1-3508-1-separate-classification-of-machinery-and-tools-used-in-semiconductor-manufacturing" tabindex="-1">§ 58.1-3508.1 Separate classification of machinery and tools used in semiconductor manufacturing. <a class="header-anchor" href="#§-58-1-3508-1-separate-classification-of-machinery-and-tools-used-in-semiconductor-manufacturing" aria-label="Permalink to &quot;§ 58.1-3508.1 Separate classification of machinery and tools used in semiconductor manufacturing.&quot;">​</a></h1><p>Machinery and tools used in semiconductor manufacturing shall constitute a classification for local taxation separate from other classifications of machinery and tools as defined in § <a href="http://law.lis.virginia.gov/vacode/58.1-3507/" target="_blank" rel="noreferrer">58.1-3507</a>. The governing body of any county, city or town may levy a tax on such classification of property at a different rate from the tax levied on other machinery and tools. The rate of tax and the rate of assessment shall not exceed that applicable generally to machinery and tools.</p><p>1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0971" target="_blank" rel="noreferrer">971</a>; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0077" target="_blank" rel="noreferrer">77</a>.</p>',3),r=[i];function c(s,l,d,f,h,u){return e(),t("div",null,r)}const p=a(n,[["render",c]]);export{_ as __pageData,p as default};
