import{_ as e,o as a,c as n,O as o}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 58.1-3508.4 Separate classification of machinery and tools used in manufacturing or processing materials, components, or equipment for national defense.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-3508.4.md","filePath":"vacode/58.1-3508.4.md"}'),t={name:"vacode/58.1-3508.4.md"},r=o('<h1 id="§-58-1-3508-4-separate-classification-of-machinery-and-tools-used-in-manufacturing-or-processing-materials-components-or-equipment-for-national-defense" tabindex="-1">§ 58.1-3508.4 Separate classification of machinery and tools used in manufacturing or processing materials, components, or equipment for national defense. <a class="header-anchor" href="#§-58-1-3508-4-separate-classification-of-machinery-and-tools-used-in-manufacturing-or-processing-materials-components-or-equipment-for-national-defense" aria-label="Permalink to &quot;§ 58.1-3508.4 Separate classification of machinery and tools used in manufacturing or processing materials, components, or equipment for national defense.&quot;">​</a></h1><p>Machinery and tools, including repair and replacement parts, designed and used directly in manufacturing or processing materials, components, or equipment for national defense are hereby declared to be a separate class of property and shall constitute a classification for local taxation separate from other classifications of machinery and tools as defined in § <a href="http://law.lis.virginia.gov/vacode/58.1-3507/" target="_blank" rel="noreferrer">58.1-3507</a>. The governing body of any county, city, or town may levy a tax on such machinery and tools at a different rate from that levied on other machinery and tools. The rate of tax imposed by the county, city, or town on such machinery and tools shall not exceed that applicable to the general class of machinery and tools.</p><p>2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0875" target="_blank" rel="noreferrer">875</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0877" target="_blank" rel="noreferrer">877</a>.</p>',3),i=[r];function s(c,l,d,f,p,m){return a(),n("div",null,i)}const _=e(t,[["render",s]]);export{u as __pageData,_ as default};
