import{_ as e,o,c as r,O as i}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 58.1-3617 Churches and religious bodies.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-3617.md","filePath":"vacode/58.1-3617.md"}'),a={name:"vacode/58.1-3617.md"},s=i('<h1 id="§-58-1-3617-churches-and-religious-bodies" tabindex="-1">§ 58.1-3617 Churches and religious bodies. <a class="header-anchor" href="#§-58-1-3617-churches-and-religious-bodies" aria-label="Permalink to &quot;§ 58.1-3617 Churches and religious bodies.&quot;">​</a></h1><p>Any church, religious association or religious denomination operated exclusively on a nonprofit basis for charitable, religious or educational purposes is hereby classified as a religious and charitable organization. Notwithstanding § <a href="http://law.lis.virginia.gov/vacode/58.1-3609/" target="_blank" rel="noreferrer">58.1-3609</a>, only property of such association or denomination used exclusively for charitable, religious or educational purposes shall be so exempt from taxation.</p><p>Motor vehicles owned or leased by churches and used predominantly for church purposes, are hereby classified as property used by its owner for religious purposes.</p><p>For purposes of this section, property of a church, religious association or religious denomination, or religious body owned or leased in the name of an incorporated church or religious body or corporation mentioned in § <a href="http://law.lis.virginia.gov/vacode/57-16.1/" target="_blank" rel="noreferrer">57-16.1</a>, a duly designated ecclesiastical officer, or a trustee of an unincorporated church or religious body shall be deemed to be owned by such church, association or denomination or religious body.</p><p>Code 1950, §§ 58-12.24, 58-12.86; 1974, c. 469; 1978, c. 216; 1984, c. 675; 1987, c. 533; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0329" target="_blank" rel="noreferrer">329</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0928" target="_blank" rel="noreferrer">928</a>.</p>',5),t=[s];function n(c,l,d,h,u,p){return o(),r("div",null,t)}const _=e(a,[["render",n]]);export{f as __pageData,_ as default};
