import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 58.1-3818 Admissions tax in counties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-3818.md","filePath":"vacode/58.1-3818.md"}'),r={name:"vacode/58.1-3818.md"},n=i('<h1 id="§-58-1-3818-admissions-tax-in-counties" tabindex="-1">§ 58.1-3818 Admissions tax in counties. <a class="header-anchor" href="#§-58-1-3818-admissions-tax-in-counties" aria-label="Permalink to &quot;§ 58.1-3818 Admissions tax in counties.&quot;">​</a></h1><p>A. Any county, except as provided in subsection C, is hereby authorized to levy a tax on admissions charged for attendance at any event. The tax shall not exceed 10 percent of the amount of charge for admission to any such event. Notwithstanding any other provisions of law, the governing bodies of such counties shall prescribe by ordinance the terms, conditions, and amount of such tax and may classify between events conducted for charitable purposes and events conducted for noncharitable purposes.</p><p>B. Notwithstanding the provisions of subsection A, localities may, by ordinance, elect not to levy an admissions tax on admission to an event, provided that the purpose of the event is solely to raise money for charitable purposes and that the net proceeds derived from the event will be transferred to an entity or entities that are exempt from sales and use tax pursuant to § <a href="/vacode/58.1-609.11/">58.1-609.11</a>.</p><p>C. No tax under this section shall be authorized in any county in which a state sales and use tax, in addition to the taxes authorized pursuant to §§ <a href="/vacode/58.1-603/">58.1-603</a> and <a href="/vacode/58.1-604/">58.1-604</a>, is imposed at a rate of at least one percent, a portion of which is dedicated to the promotion of tourism.</p><p>Code 1950, § 58-404.2; 1971, c. 212; 1977, c. 573; 1978, c. 432; 1984, c. 675; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0201" target="_blank" rel="noreferrer">201</a>; 1998, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0150" target="_blank" rel="noreferrer">150</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0532" target="_blank" rel="noreferrer">532</a>; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0986" target="_blank" rel="noreferrer">986</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0485" target="_blank" rel="noreferrer">485</a>; 2003, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0757" target="_blank" rel="noreferrer">757</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0758" target="_blank" rel="noreferrer">758</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0106" target="_blank" rel="noreferrer">106</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0813" target="_blank" rel="noreferrer">813</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1214" target="_blank" rel="noreferrer">1214</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1263" target="_blank" rel="noreferrer">1263</a>.</p>',5),o=[n];function s(c,l,h,d,f,p){return t(),a("div",null,o)}const v=e(r,[["render",s]]);export{u as __pageData,v as default};
