import{_ as e,o as a,c as o,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 58.1-3834 Apportionment of food and beverage or meals tax.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-3834.md","filePath":"vacode/58.1-3834.md"}'),r={name:"vacode/58.1-3834.md"},n=t('<h1 id="§-58-1-3834-apportionment-of-food-and-beverage-or-meals-tax" tabindex="-1">§ 58.1-3834 Apportionment of food and beverage or meals tax. <a class="header-anchor" href="#§-58-1-3834-apportionment-of-food-and-beverage-or-meals-tax" aria-label="Permalink to &quot;§ 58.1-3834 Apportionment of food and beverage or meals tax.&quot;">​</a></h1><p>In any case where a business is located partially within two or more local jurisdictions by reason of the boundary line between the local jurisdictions passing through such place of business, and one or more of the local jurisdictions imposes the food and beverage or meals tax, the tax rate shall be computed by applying the apportionment formula in § <a href="/vacode/58.1-3709/">58.1-3709</a> to the food and beverage or meals tax rate of each applicable local jurisdiction. Such apportioned rate shall be rounded to the nearest one-half percent.</p><p>1993, c. 104; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1214" target="_blank" rel="noreferrer">1214</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1263" target="_blank" rel="noreferrer">1263</a>.</p>',3),i=[n];function s(l,c,d,p,f,h){return a(),o("div",null,i)}const b=e(r,[["render",s]]);export{m as __pageData,b as default};
