import{_ as e,o as t,c as r,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 58.1-399.2 Tiered partners.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-399.2.md","filePath":"vacode/58.1-399.2.md"}'),n={name:"vacode/58.1-399.2.md"},i=a('<h1 id="§-58-1-399-2-tiered-partners" tabindex="-1">§ 58.1-399.2 Tiered partners. <a class="header-anchor" href="#§-58-1-399-2-tiered-partners" aria-label="Permalink to &quot;§ 58.1-399.2 Tiered partners.&quot;">​</a></h1><p>A. The following categories of partners shall be subject to the reporting and payment requirements specified in § <a href="/vacode/58.1-399/">58.1-399</a>, entitled to make elections as provided in § <a href="/vacode/58.1-399.1/">58.1-399.1</a>, and entitled to elect an alternative reporting and payment method as provided in § <a href="/vacode/58.1-399.3/">58.1-399.3</a>:</p><ol><li><p>Any direct tiered partner of an audited partnership;</p></li><li><p>Any indirect tiered partner of an audited partnership; and</p></li><li><p>Any partner of a partner specified in subdivision 1 or 2.</p></li></ol><p>B. A partner subject to the provisions of subsection A shall make required reports and payments no later than 90 days after the time for filing and providing statements to tiered partners and their partners pursuant to the provisions of § 6226 of the Internal Revenue Code and any regulations promulgated thereunder. The Department may establish procedures and deadlines for reports and payments required pursuant to this section.</p><p>2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1030" target="_blank" rel="noreferrer">1030</a>.</p>',5),o=[i];function s(d,p,l,c,h,_){return t(),r("div",null,o)}const m=e(n,[["render",s]]);export{u as __pageData,m as default};
