import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 58.1-399 Reporting and payment requirements for a partnership subject to a final federal adjustment.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-399.md","filePath":"vacode/58.1-399.md"}'),n={name:"vacode/58.1-399.md"},i=r('<h1 id="§-58-1-399-reporting-and-payment-requirements-for-a-partnership-subject-to-a-final-federal-adjustment" tabindex="-1">§ 58.1-399 Reporting and payment requirements for a partnership subject to a final federal adjustment. <a class="header-anchor" href="#§-58-1-399-reporting-and-payment-requirements-for-a-partnership-subject-to-a-final-federal-adjustment" aria-label="Permalink to &quot;§ 58.1-399 Reporting and payment requirements for a partnership subject to a final federal adjustment.&quot;">​</a></h1><p>A. Except as otherwise provided in this article, any final federal adjustment shall be reported pursuant to the provisions of subsection B. This subsection shall not apply to a final federal adjustment for which election has been properly made pursuant to § <a href="/vacode/58.1-399.1/">58.1-399.1</a>.</p><p>B. No later than 90 days after the final determination date, a partnership shall:</p><ol><li><p>File with the Department a completed federal adjustments report, which shall include any information required by the Department;</p></li><li><p>Notify each direct partner of its distributive share of the final federal adjustments and provide to each direct partner any other information required by the Department;</p></li><li><p>File an amended composite return pursuant to § <a href="/vacode/58.1-395/">58.1-395</a> if such return previously was filed on behalf of nonresident partners;</p></li><li><p>File an amended return pursuant to § <a href="/vacode/58.1-392/">58.1-392</a>; and</p></li><li><p>Pay any additional amount that may be required pursuant to the provisions of §§ <a href="/vacode/58.1-395/">58.1-395</a> and <a href="/vacode/58.1-486.2/">58.1-486.2</a>.</p></li></ol><p>C. Except as provided under § <a href="/vacode/58.1-321/">58.1-321</a>, no later than one year after the final determination date, each direct partner subject to tax pursuant to the provisions of Article 2 (§ <a href="/vacode/58.1-320/">58.1-320</a> et seq.), 6 (§ <a href="/vacode/58.1-360/">58.1-360</a> et seq.), or 10 (§ <a href="/vacode/58.1-400/">58.1-400</a>) shall:</p><ol><li><p>File a federal adjustments report that identifies the distributive share of adjustments reported to such direct partner under subdivision B 2; and</p></li><li><p>Pay any additional amount of tax due as if final federal adjustments had been properly reported, including any penalty and interest due under this title. Such payment may be reduced by any credit for related amounts paid or withheld and remitted on behalf of the direct partner pursuant to subdivision B 3, 4, or 5.</p></li></ol><p>2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1030" target="_blank" rel="noreferrer">1030</a>.</p>',7),o=[i];function d(s,p,l,h,u,f){return a(),t("div",null,o)}const _=e(n,[["render",d]]);export{m as __pageData,_ as default};
