import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 58.1-3995 Effect of application for correction of assessment or appeal upon applications for local permits and licenses.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-3995.md","filePath":"vacode/58.1-3995.md"}'),r={name:"vacode/58.1-3995.md"},i=o('<h1 id="§-58-1-3995-effect-of-application-for-correction-of-assessment-or-appeal-upon-applications-for-local-permits-and-licenses" tabindex="-1">§ 58.1-3995 Effect of application for correction of assessment or appeal upon applications for local permits and licenses. <a class="header-anchor" href="#§-58-1-3995-effect-of-application-for-correction-of-assessment-or-appeal-upon-applications-for-local-permits-and-licenses" aria-label="Permalink to &quot;§ 58.1-3995 Effect of application for correction of assessment or appeal upon applications for local permits and licenses.&quot;">​</a></h1><p>A. Except as otherwise provided in subsection B, no county, city or town shall deny to any person a permit or license to which such person otherwise is entitled solely on the grounds that such person has failed to pay taxes, penalties and interest due such locality, as applicable, when and to the extent that such taxes, penalties and interest are the subject of a pending, bona fide: (i) application for correction of an assessment of taxes pursuant to § <a href="http://law.lis.virginia.gov/vacode/58.1-3980/" target="_blank" rel="noreferrer">58.1-3980</a>; (ii) appeal of a local license tax pursuant to § <a href="http://law.lis.virginia.gov/vacode/58.1-3703.1/" target="_blank" rel="noreferrer">58.1-3703.1</a>; (iii) appeal by a political subdivision pursuant to § <a href="http://law.lis.virginia.gov/vacode/58.1-3982/" target="_blank" rel="noreferrer">58.1-3982</a> of a correction of assessment of local taxes; (iv) appeal of a local tax or local business tax pursuant to § <a href="http://law.lis.virginia.gov/vacode/58.1-3983.1/" target="_blank" rel="noreferrer">58.1-3983.1</a>; (v) an application pursuant to § <a href="http://law.lis.virginia.gov/vacode/58.1-3984/" target="_blank" rel="noreferrer">58.1-3984</a> for correction of a local tax or local business tax as those terms are defined in § <a href="http://law.lis.virginia.gov/vacode/58.1-3983.1/" target="_blank" rel="noreferrer">58.1-3983.1</a>; or (vi) an application for correction or equalization of an assessment with respect to real property pursuant to § <a href="http://law.lis.virginia.gov/vacode/58.1-3350/" target="_blank" rel="noreferrer">58.1-3350</a>.</p><p>B. Nothing in this section shall be construed to require: (i) the issuance by a county, city or town of a local vehicle license that has been withheld pursuant to the provisions of § <a href="http://law.lis.virginia.gov/vacode/46.2-752/" target="_blank" rel="noreferrer">46.2-752</a> or any subsection thereof; or (ii) the issuance by the Commissioner of Motor Vehicles of a vehicle registration or renewal of registration with respect to a vehicle as to which registration has been withheld pursuant to the provisions of subsection J of § <a href="http://law.lis.virginia.gov/vacode/46.2-752/" target="_blank" rel="noreferrer">46.2-752</a>.</p><p>C. Nothing in this section shall be construed to limit the ability of a locality to exercise powers granted under general law, including without limitation §§ <a href="http://law.lis.virginia.gov/vacode/15.2-2286/" target="_blank" rel="noreferrer">15.2-2286</a> and <a href="http://law.lis.virginia.gov/vacode/58.1-3700/" target="_blank" rel="noreferrer">58.1-3700</a>, to deny a license or permit to a taxpayer who is delinquent in the payment of taxes, penalties, or interest and who does not have presently pending a bona fide application or appeal enumerated in subsection A with respect to such taxes, penalties, or interest.</p><p>2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0902" target="_blank" rel="noreferrer">902</a>.</p>',5),n=[i];function s(l,c,p,f,h,d){return a(),t("div",null,n)}const g=e(r,[["render",s]]);export{v as __pageData,g as default};
