import{_ as e,o as t,c as o,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 58.1-4008 Employees of the Department; background investigations of employees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-4008.md","filePath":"vacode/58.1-4008.md"}'),a={name:"vacode/58.1-4008.md"},i=r('<h1 id="§-58-1-4008-employees-of-the-department-background-investigations-of-employees" tabindex="-1">§ 58.1-4008 Employees of the Department; background investigations of employees. <a class="header-anchor" href="#§-58-1-4008-employees-of-the-department-background-investigations-of-employees" aria-label="Permalink to &quot;§ 58.1-4008 Employees of the Department; background investigations of employees.&quot;">​</a></h1><p>All persons employed by the Department shall be fingerprinted before, and as a condition of, employment. These fingerprints shall be submitted to the Federal Bureau of Investigation for a National Criminal Records search and to the Department of State Police for a Virginia Criminal History Records search. All board members, officers and employees of any vendor to the Department of lottery on-line or instant ticket goods or services working directly on a contract with the Department for such goods or services shall be fingerprinted, and such fingerprints shall be submitted to the Federal Bureau of Investigation for a National Criminal Records search conducted by the chief security officer of the Virginia Lottery. A background investigation shall be conducted by the chief security officer of the Virginia Lottery on every applicant prior to employment by the Department. However, all division directors of the Virginia Lottery and employees of the Virginia Lottery performing duties primarily related to security matters shall be subject to a background investigation report conducted by the Department of State Police prior to employment by the Department. The Department of State Police shall be reimbursed by the Virginia Lottery for the cost of investigations conducted pursuant to this section or § <a href="http://law.lis.virginia.gov/vacode/58.1-4005/" target="_blank" rel="noreferrer">58.1-4005</a>. No person who has been convicted of a felony, bookmaking or other forms of illegal gambling, or of a crime involving moral turpitude shall be employed by the Department or on contracts with vendors described in this section.</p><p>1987, c. 531; 1989, c. 478; 1992, c. 449; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0555" target="_blank" rel="noreferrer">555</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0225" target="_blank" rel="noreferrer">225</a>.</p>',3),n=[i];function s(l,c,d,p,f,h){return t(),o("div",null,n)}const b=e(a,[["render",s]]);export{g as __pageData,b as default};
