import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 58.1-4011 Meaning of \\"gross receipts.\\".","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-4011.md","filePath":"vacode/58.1-4011.md"}'),r={name:"vacode/58.1-4011.md"},s=o('<h1 id="§-58-1-4011-meaning-of-gross-receipts" tabindex="-1">§ 58.1-4011 Meaning of &quot;gross receipts.&quot;. <a class="header-anchor" href="#§-58-1-4011-meaning-of-gross-receipts" aria-label="Permalink to &quot;§ 58.1-4011 Meaning of &quot;gross receipts.&quot;.&quot;">​</a></h1><p>A. Notwithstanding the provisions of Chapter 37 (§ <a href="http://law.lis.virginia.gov/vacode/58.1-3700/" target="_blank" rel="noreferrer">58.1-3700</a> et seq.) or § <a href="http://law.lis.virginia.gov/vacode/58.1-4025/" target="_blank" rel="noreferrer">58.1-4025</a> relating to local license taxes, the term &quot;gross receipts&quot; as used in Chapter 37 shall include only the compensation actually paid to a licensed sales agent as provided by rule or regulation adopted by the Board consistent with the provisions of subdivision A 11 of § <a href="http://law.lis.virginia.gov/vacode/58.1-4007/" target="_blank" rel="noreferrer">58.1-4007</a>.</p><p>B. Unless otherwise provided by contract, any person licensed as a lottery agent who makes rental payments for the business premises on which state lottery tickets are sold on the basis of retail sales shall have that portion of rental payment based on sales of state lottery tickets or shares computed on the basis of the compensation received as a lottery agent from the Virginia Lottery.</p><p>1987, c. 531; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0225" target="_blank" rel="noreferrer">225</a>.</p>',4),i=[s];function n(l,c,p,h,d,g){return t(),a("div",null,i)}const v=e(r,[["render",n]]);export{f as __pageData,v as default};
