import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 58.1-4014 Price of tickets or shares; who may sell; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-4014.md","filePath":"vacode/58.1-4014.md"}'),o={name:"vacode/58.1-4014.md"},s=r('<h1 id="§-58-1-4014-price-of-tickets-or-shares-who-may-sell-penalty" tabindex="-1">§ 58.1-4014 Price of tickets or shares; who may sell; penalty. <a class="header-anchor" href="#§-58-1-4014-price-of-tickets-or-shares-who-may-sell-penalty" aria-label="Permalink to &quot;§ 58.1-4014 Price of tickets or shares; who may sell; penalty.&quot;">​</a></h1><p>No person shall sell a ticket or share at any price or at any location other than that fixed by rules and regulations of the Department. No person other than a licensed lottery sales agent or his employee shall sell lottery tickets or shares, except that nothing in this section shall be construed to prevent any person from giving lottery tickets or shares to another person over the age of 18 years as a gift. No person shall operate a ticket courier service in the Commonwealth.</p><p>Any person convicted of violating this section is guilty of a Class 1 misdemeanor.</p><p>1987, c. 531; 1992, c. 449; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0630" target="_blank" rel="noreferrer">630</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0598" target="_blank" rel="noreferrer">598</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0461" target="_blank" rel="noreferrer">461</a>.</p>',4),i=[s];function n(l,c,h,p,_,f){return t(),a("div",null,i)}const m=e(o,[["render",n]]);export{g as __pageData,m as default};
