import{_ as e,o as i,c as t,O as r}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 58.1-4027 Judicial review.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-4027.md","filePath":"vacode/58.1-4027.md"}'),a={name:"vacode/58.1-4027.md"},o=r('<h1 id="§-58-1-4027-judicial-review" tabindex="-1">§ 58.1-4027 Judicial review. <a class="header-anchor" href="#§-58-1-4027-judicial-review" aria-label="Permalink to &quot;§ 58.1-4027 Judicial review.&quot;">​</a></h1><p>The action of the Board in (i) granting or denying a license or registration or in suspending or revoking any license or registration under the provisions of this article and (ii) granting, denying, suspending, or revoking any permit or imposing any penalty pursuant to Article 2 (§ <a href="/vacode/58.1-4030/">58.1-4030</a> et seq.) shall be subject to review in accordance with the provisions of the Administrative Process Act (§ <a href="/vacode/2.2-4000/">2.2-4000</a> et seq.). Such review shall be limited to the evidential record of the proceedings provided by the Board. Both the petitioner and the Board shall have the right to appeal to the Court of Appeals from any order of the court.</p><p>1987, c. 531; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1218" target="_blank" rel="noreferrer">1218</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1256" target="_blank" rel="noreferrer">1256</a>.</p>',3),n=[o];function c(s,d,l,h,p,_){return i(),t("div",null,n)}const f=e(a,[["render",c]]);export{v as __pageData,f as default};
