import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 58.1-4033 Renewals of permits.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-4033.md","filePath":"vacode/58.1-4033.md"}'),i={name:"vacode/58.1-4033.md"},o=t('<h1 id="§-58-1-4033-renewals-of-permits" tabindex="-1">§ 58.1-4033 Renewals of permits. <a class="header-anchor" href="#§-58-1-4033-renewals-of-permits" aria-label="Permalink to &quot;§ 58.1-4033 Renewals of permits.&quot;">​</a></h1><p>A. A permit issued pursuant to § <a href="/vacode/58.1-4032/">58.1-4032</a> shall be valid for three years from the date issued.</p><p>B. At least 60 days before the expiration of a permit, the permit holder shall submit a renewal application, on forms prescribed by the Director, with a nonrefundable renewal fee of $200,000.</p><p>C. The Director may deny a permit renewal if he finds grounds for denial as described in subsection F of § <a href="/vacode/58.1-4032/">58.1-4032</a>. The Director&#39;s action shall be final unless appealed in accordance with § <a href="/vacode/58.1-4007/">58.1-4007</a>.</p><p>D. The Director shall make a determination on an application for a renewal of a sports betting permit within 60 days of receipt. The Director&#39;s action shall be final unless appealed in accordance with § <a href="/vacode/58.1-4007/">58.1-4007</a>.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1218" target="_blank" rel="noreferrer">1218</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1256" target="_blank" rel="noreferrer">1256</a>.</p>',6),n=[o];function s(l,c,p,d,f,h){return a(),r("div",null,n)}const u=e(i,[["render",s]]);export{m as __pageData,u as default};
