import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 58.1-405 Corporations transacting or conducting entire business within this Commonwealth.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-405.md","filePath":"vacode/58.1-405.md"}'),o={name:"vacode/58.1-405.md"},n=i('<h1 id="§-58-1-405-corporations-transacting-or-conducting-entire-business-within-this-commonwealth" tabindex="-1">§ 58.1-405 Corporations transacting or conducting entire business within this Commonwealth. <a class="header-anchor" href="#§-58-1-405-corporations-transacting-or-conducting-entire-business-within-this-commonwealth" aria-label="Permalink to &quot;§ 58.1-405 Corporations transacting or conducting entire business within this Commonwealth.&quot;">​</a></h1><p>Except as provided in § <a href="/vacode/58.1-405.1/">58.1-405.1</a>, if the entire business of the corporation is transacted or conducted within the Commonwealth, the tax imposed by this chapter shall be upon the entire Virginia taxable income of such corporation for each taxable year; however, if such corporation is certified by the Virginia Economic Development Partnership Authority as an eligible company pursuant to § <a href="/vacode/58.1-405.1/">58.1-405.1</a>, it may elect to (i) apportion its income between qualified localities, as defined in § <a href="/vacode/58.1-405.1/">58.1-405.1</a>, and other localities in the Commonwealth, provided that it shall not apportion any of its income to a state other than Virginia and (ii) utilize any modification for which it may be eligible pursuant to the provisions of § <a href="/vacode/58.1-408/">58.1-408</a>, <a href="/vacode/58.1-417/">58.1-417</a>, <a href="/vacode/58.1-418/">58.1-418</a>, <a href="/vacode/58.1-419/">58.1-419</a>, <a href="/vacode/58.1-420/">58.1-420</a>, <a href="/vacode/58.1-422/">58.1-422</a>, <a href="https://law.lis.virginia.gov/vacode/58.1-422.1/" target="_blank" rel="noreferrer">58.1-422.1</a>, or <a href="https://law.lis.virginia.gov/vacode/title58.1/chapter3/section58.1-422.2/" target="_blank" rel="noreferrer">58.1-422.2</a>, as applicable. The entire business of the corporation shall be deemed to have been transacted or conducted within the Commonwealth if such corporation is not subject in any other state to a net income tax, a franchise tax measured by net income, or a franchise tax for the privilege of doing business.</p><p>Code 1950, § 58-151.033; 1971, Ex. Sess., c. 171; 1981, c. 402; 1984, c. 675; 2018, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0801" target="_blank" rel="noreferrer">801</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0802" target="_blank" rel="noreferrer">802</a>.</p>',3),r=[n];function s(c,h,l,d,p,f){return t(),a("div",null,r)}const g=e(o,[["render",s]]);export{u as __pageData,g as default};
