import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 58.1-4102 Powers and duties of the Board; regulations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-4102.md","filePath":"vacode/58.1-4102.md"}'),r={name:"vacode/58.1-4102.md"},s=o('<h1 id="§-58-1-4102-powers-and-duties-of-the-board-regulations" tabindex="-1">§ 58.1-4102 Powers and duties of the Board; regulations. <a class="header-anchor" href="#§-58-1-4102-powers-and-duties-of-the-board-regulations" aria-label="Permalink to &quot;§ 58.1-4102 Powers and duties of the Board; regulations.&quot;">​</a></h1><p>The Board shall have the power and duty to:</p><ol><li><p>Issue permits and licenses under this chapter and supervise all gaming operations licensed under the provisions of this chapter, including all persons conducting or participating in any gaming operation. The Board shall employ such persons to be present during gaming operations as are necessary to ensure that such gaming operations are conducted with order and the highest degree of integrity.</p></li><li><p>Adopt regulations regarding the conditions under which casino gaming shall be conducted in the Commonwealth and all such other regulations it deems necessary and appropriate to further the purposes of this chapter.</p></li><li><p>Issue an operator&#39;s license only to a person who meets the criteria of § <a href="/vacode/58.1-4107/">58.1-4107</a>.</p></li><li><p>Issue subpoenas for the attendance of witnesses before the Board, administer oaths, and compel production of records or other documents and testimony of such witnesses whenever in the judgment of the Board it is necessary to do so for the effectual discharge of its duties.</p></li><li><p>Order such audits as it deems necessary and desirable.</p></li><li><p>Provide for the withholding of the applicable amount of state and federal income tax of persons claiming a prize or payoff for winning a game and establish the thresholds for such withholdings.</p></li></ol><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1197" target="_blank" rel="noreferrer">1197</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1248" target="_blank" rel="noreferrer">1248</a>.</p>',4),i=[s];function n(d,h,l,c,p,u){return a(),t("div",null,i)}const m=e(r,[["render",n]]);export{g as __pageData,m as default};
