import{_ as e,o as i,c as t,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 58.1-4107.1 Regional Improvement Commission.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-4107.1.md","filePath":"vacode/58.1-4107.1.md"}'),a={name:"vacode/58.1-4107.1.md"},s=o('<h1 id="§-58-1-4107-1-regional-improvement-commission" tabindex="-1">§ 58.1-4107.1 Regional Improvement Commission. <a class="header-anchor" href="#§-58-1-4107-1-regional-improvement-commission" aria-label="Permalink to &quot;§ 58.1-4107.1 Regional Improvement Commission.&quot;">​</a></h1><p>There is hereby established the Regional Improvement Commission (the Commission). The membership of the Commission shall consist of one member appointed by the local governing body of each jurisdiction composing the transportation district created pursuant to the Transportation District Act of 1964 (§ <a href="/vacode/33.2-1900/">33.2-1900</a> et seq.) that includes the eligible host city described in subdivision A 3 of § <a href="/vacode/58.1-4107/">58.1-4107</a>. Each member shall be appointed to serve a two-year term. Notwithstanding the provisions of subdivision B 1 of § <a href="/vacode/58.1-4125/">58.1-4125</a>, for a casino gaming establishment located in the eligible host city described in subdivision A 3 of § <a href="/vacode/58.1-4107/">58.1-4107</a>, such transfer, otherwise returned to the city where it was collected, shall instead be made to the Commission. The purpose of the Commission shall be to (i) receive disbursements made to it; (ii) establish funding priorities for member localities related to improvements in the areas of education, transportation, and public safety; and (iii) make annual payments divided equally among the jurisdictions to fund the established priorities as determined by the Commission.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1197" target="_blank" rel="noreferrer">1197</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1248" target="_blank" rel="noreferrer">1248</a>.</p>',3),n=[s];function r(m,c,d,l,h,p){return i(),t("div",null,n)}const b=e(a,[["render",r]]);export{_ as __pageData,b as default};
