import{_ as e,o as a,c as o,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 58.1-4134 On-premises mobile casino gaming accounts.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-4134.md","filePath":"vacode/58.1-4134.md"}'),n={name:"vacode/58.1-4134.md"},t=i('<h1 id="§-58-1-4134-on-premises-mobile-casino-gaming-accounts" tabindex="-1">§ 58.1-4134 On-premises mobile casino gaming accounts. <a class="header-anchor" href="#§-58-1-4134-on-premises-mobile-casino-gaming-accounts" aria-label="Permalink to &quot;§ 58.1-4134 On-premises mobile casino gaming accounts.&quot;">​</a></h1><p>A. A casino gaming operator may offer on-premises mobile casino gaming only to an individual who has established an on-premises mobile casino gaming account and uses such account to place wagers as follows:</p><ol><li><p>Any wager shall be placed directly with the casino gaming operator by the account holder;</p></li><li><p>The casino gaming operator shall verify the account holder&#39;s physical presence on the premises of the casino gaming establishment; and</p></li><li><p>The account holder shall provide the casino licensee with the correct authentication information for access to the wagering account.</p></li></ol><p>B. A casino gaming operator shall not accept a wager in an amount in excess of funds on deposit in the account of the individual placing the wager.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1197" target="_blank" rel="noreferrer">1197</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1248" target="_blank" rel="noreferrer">1248</a>.</p>',5),s=[t];function c(r,l,p,h,g,m){return a(),o("div",null,s)}const u=e(n,[["render",c]]);export{_ as __pageData,u as default};
