import{_ as e,o,c as a,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 58.1-4136 Assistance to people with gambling problem.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-4136.md","filePath":"vacode/58.1-4136.md"}'),n={name:"vacode/58.1-4136.md"},i=t('<h1 id="§-58-1-4136-assistance-to-people-with-gambling-problem" tabindex="-1">§ 58.1-4136 Assistance to people with gambling problem. <a class="header-anchor" href="#§-58-1-4136-assistance-to-people-with-gambling-problem" aria-label="Permalink to &quot;§ 58.1-4136 Assistance to people with gambling problem.&quot;">​</a></h1><p>A. In order to assist those persons who may have a gambling problem, a casino gaming operator shall:</p><ol><li><p>Cause the words &quot;If you or someone you know has a gambling problem and wants help, call 1-800-GAMBLER,&quot; or some comparable language approved by the Department, which language shall include the words &quot;gambling problem&quot; and &quot;call 1-800 GAMBLER,&quot; to be displayed prominently at log-on and log-off times to any person visiting or logged onto on-premises mobile casino gaming; and</p></li><li><p>Provide a mechanism by which an account holder may establish the following controls on wagering activity through the wagering account:</p></li></ol><p>a. A limit on the amount of money deposited within a specified period of time and the length of time the account holder will be unable to participate in gaming if the holder reaches the established deposit limit; and</p><p>b. A temporary suspension of gaming through the account for any number of hours or days.</p><p>B. The casino gaming operator shall not send gaming-related electronic mail to an account holder while gaming through his account is suspended, if the suspension is for at least 72 hours. The casino gaming operator shall provide a mechanism by which an account holder may change these controls, except that, while gaming through the wagering account is suspended, the account holder may not change gaming controls until the suspension expires, but the account holder shall continue to have access to the account and shall be permitted to withdraw funds from the account upon proper application therefor.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1197" target="_blank" rel="noreferrer">1197</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1248" target="_blank" rel="noreferrer">1248</a>.</p>',7),s=[i];function r(l,h,c,p,g,m){return o(),a("div",null,s)}const b=e(n,[["render",r]]);export{u as __pageData,b as default};
