import{_ as e,o as r,c as i,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 58.1-438.1 Tax credit for vehicle emissions testing equipment, clean-fuel vehicles and certain refueling property.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-438.1.md","filePath":"vacode/58.1-438.1.md"}'),a={name:"vacode/58.1-438.1.md"},n=t('<h1 id="§-58-1-438-1-tax-credit-for-vehicle-emissions-testing-equipment-clean-fuel-vehicles-and-certain-refueling-property" tabindex="-1">§ 58.1-438.1 Tax credit for vehicle emissions testing equipment, clean-fuel vehicles and certain refueling property. <a class="header-anchor" href="#§-58-1-438-1-tax-credit-for-vehicle-emissions-testing-equipment-clean-fuel-vehicles-and-certain-refueling-property" aria-label="Permalink to &quot;§ 58.1-438.1 Tax credit for vehicle emissions testing equipment, clean-fuel vehicles and certain refueling property.&quot;">​</a></h1><p>Any corporation, individual or public service corporation shall be allowed a credit against the income or gross receipts taxes imposed by Subtitle I (§ <a href="http://law.lis.virginia.gov/vacode/58.1-100/" target="_blank" rel="noreferrer">58.1-100</a> et seq.) and Chapter 26 (§ <a href="http://law.lis.virginia.gov/vacode/58.1-2600/" target="_blank" rel="noreferrer">58.1-2600</a> et seq.) of Title 58.1 of (i) an amount equal to ten percent of the deduction allowed to such corporation, individual or public service corporation under Section 179A of the Internal Revenue Code for purchases of clean-fuel vehicles principally garaged in Virginia or certain refueling property placed in service in Virginia or ten percent of the costs used to compute the credit under Section 30 of the Internal Revenue Code and (ii) an amount equal to twenty percent of the purchase or lease price paid during the taxable year for equipment certified by the Department of Environmental Quality for vehicle emissions testing, located within, or within any county, city or town adjacent to, any county, city or town wherein implementation of an enhanced vehicle emissions inspection program, as defined in § <a href="http://law.lis.virginia.gov/vacode/46.2-1176/" target="_blank" rel="noreferrer">46.2-1176</a>, is required. Credits granted to a partnership or S corporation shall be passed through to the partners or shareholders, respectively. If the credit exceeds the tax liability in a year, the credit may be carried forward up to five succeeding years.</p><p>1993, c. 562; 1994, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0164" target="_blank" rel="noreferrer">164</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0875" target="_blank" rel="noreferrer">875</a>; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0100" target="_blank" rel="noreferrer">100</a>; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0350" target="_blank" rel="noreferrer">350</a>; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0599" target="_blank" rel="noreferrer">599</a>.</p>',3),o=[n];function c(l,s,p,d,h,f){return r(),i("div",null,o)}const v=e(a,[["render",c]]);export{u as __pageData,v as default};
