import{_ as e,o as a,c as o,O as t}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 58.1-464 Miscellaneous payroll period applicable to withholding in payment of certain wages; withholding on basis of average wages.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-464.md","filePath":"vacode/58.1-464.md"}'),i={name:"vacode/58.1-464.md"},n=t('<h1 id="§-58-1-464-miscellaneous-payroll-period-applicable-to-withholding-in-payment-of-certain-wages-withholding-on-basis-of-average-wages" tabindex="-1">§ 58.1-464 Miscellaneous payroll period applicable to withholding in payment of certain wages; withholding on basis of average wages. <a class="header-anchor" href="#§-58-1-464-miscellaneous-payroll-period-applicable-to-withholding-in-payment-of-certain-wages-withholding-on-basis-of-average-wages" aria-label="Permalink to &quot;§ 58.1-464 Miscellaneous payroll period applicable to withholding in payment of certain wages; withholding on basis of average wages.&quot;">​</a></h1><p>A. If wages are paid with respect to a period which is not a payroll period, the amount to be deducted and withheld shall be that applicable in the case of a miscellaneous payroll period containing a number of days including Sundays and holidays, equal to the number of days in the period with respect to which such wages are paid.</p><p>B. In any case in which wages are paid by an employer without regard to any payroll period or other period, the amount to be deducted and withheld shall be that applicable in the case of a miscellaneous payroll period containing a number of days equal to the number of days, including Sundays and holidays, which have elapsed since the date of the last payment of such wages by such employer during the calendar year, or the date of commencement of employment with such employer during such year, or January 1 of such year, whichever is the later.</p><p>C. The Tax Commissioner may, by regulations, authorize employers:</p><ol><li><p>To estimate the wages which will be paid to any employee in any quarter of the calendar year;</p></li><li><p>To determine the amount to be deducted and withheld upon each payment of wages to such employee during such quarter as if the appropriate average of the wages so estimated constituted the actual wages paid; and</p></li><li><p>To deduct and withhold upon any payment of wages to such employee during such quarter such amount as may be necessary to adjust the amount actually deducted and withheld upon the wages of such employee during such quarter to the amount that would be required to be deducted and withheld during such quarter without regard to this subsection C.</p></li></ol><p>Code 1950, § 58-151.5; 1962, c. 612; 1984, c. 675.</p>',6),s=[n];function l(r,d,h,c,p,u){return a(),o("div",null,s)}const g=e(i,[["render",l]]);export{y as __pageData,g as default};
