import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 58.1-478 Withholding tax statements for employees; employers must file annual returns with Tax Commissioner; penalties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-478.md","filePath":"vacode/58.1-478.md"}'),i={name:"vacode/58.1-478.md"},n=r('<h1 id="§-58-1-478-withholding-tax-statements-for-employees-employers-must-file-annual-returns-with-tax-commissioner-penalties" tabindex="-1">§ 58.1-478 Withholding tax statements for employees; employers must file annual returns with Tax Commissioner; penalties. <a class="header-anchor" href="#§-58-1-478-withholding-tax-statements-for-employees-employers-must-file-annual-returns-with-tax-commissioner-penalties" aria-label="Permalink to &quot;§ 58.1-478 Withholding tax statements for employees; employers must file annual returns with Tax Commissioner; penalties.&quot;">​</a></h1><p>A. Every person required to deduct and withhold from an employee&#39;s wages under this article shall furnish to each such employee in respect to the remuneration paid by such person to such employee during the calendar year, on or before January 31 of the succeeding year, or if his employment is terminated before the close of such calendar year, on the day on which the last payment of remuneration is made, a written statement in duplicate showing the following: (i) the name of such person; (ii) the name of the employee and his social security account number; (iii) the total amount of wages; and (iv) the total amount deducted and withheld under this article by such employer.</p><p>B. The written statements required to be furnished pursuant to this section in respect of any remuneration shall be furnished at such other times, shall contain such other information, and shall be in such form as the Tax Commissioner may by regulations prescribe.</p><p>C. 1. Every employer shall file an annual return with the Tax Commissioner, setting forth such information as the Tax Commissioner may require, not later than January 31 of the calendar year succeeding the calendar year in which wages were withheld from employees, and such annual return shall be accompanied by an additional copy of each of the written statements furnished to each employee under subsections A and B.</p><ol start="2"><li>Every employer shall file the annual return and copies of written statements required under this subsection using an electronic medium using a format prescribed by the Tax Commissioner. Waivers shall be granted only if the Tax Commissioner finds that this requirement creates an unreasonable burden on the employer. All requests for waiver shall be submitted to the Tax Commissioner in writing.</li></ol><p>D. The Tax Commissioner shall have the authority to require every employer to furnish the names and social security numbers of all employees whose wages or withholding amounts for the taxable year are below levels specified by the Tax Commissioner.</p><p>Code 1950, § 58-151.14; 1962, c. 612; 1971, Ex. Sess., c. 171; 1984, c. 675; 1987, c. 9; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0335" target="_blank" rel="noreferrer">335</a>; 2001, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0297" target="_blank" rel="noreferrer">297</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0307" target="_blank" rel="noreferrer">307</a>; 2010, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0036" target="_blank" rel="noreferrer">36</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0151" target="_blank" rel="noreferrer">151</a>; 2016, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0660" target="_blank" rel="noreferrer">660</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0676" target="_blank" rel="noreferrer">676</a>.</p>',7),s=[n];function o(l,h,m,c,u,p){return t(),a("div",null,s)}const g=e(i,[["render",o]]);export{f as __pageData,g as default};
