import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 58.1-608.1 Refund authorized for certain building materials.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-608.1.md","filePath":"vacode/58.1-608.1.md"}'),r={name:"vacode/58.1-608.1.md"},i=o('<h1 id="§-58-1-608-1-refund-authorized-for-certain-building-materials" tabindex="-1">§ 58.1-608.1 Refund authorized for certain building materials. <a class="header-anchor" href="#§-58-1-608-1-refund-authorized-for-certain-building-materials" aria-label="Permalink to &quot;§ 58.1-608.1 Refund authorized for certain building materials.&quot;">​</a></h1><p>A. From July 1, 1993, through June 30, 2004, any organization meeting the following conditions and criteria may apply to the Department of Taxation for a refund of any taxes paid on tangible personal property pursuant to this chapter:</p><ol><li><p>The organization is exempt from taxation under § 501(c)(3) of the Internal Revenue Code;</p></li><li><p>The organization is organized and operated primarily to acquire land and purchase materials to erect or rehabilitate low-cost homes on such land; and</p></li><li><p>The homes are sold at cost on a nondiscriminatory basis to persons who otherwise would be unable to afford to buy a home through conventional means.</p></li></ol><p>B. Notwithstanding the provisions of subsection A, an organization exempt from taxation under § 501(c)(3) of the Internal Revenue Code may apply for a refund of any sales and use tax paid on tangible personal property used to repair or rehabilitate homes owned and occupied by low-income persons who could not otherwise afford to finance the rehabilitation or repair of their homes.</p><p>C. The Department of Taxation may require that any organization submit sales tax receipts along with the refund application to qualify for the refund authorized pursuant to this section.</p><p>D. The provisions of this section do not apply to any organization to the extent that the organization already is exempt from taxes imposed on tangible personal property by this chapter pursuant to § <a href="http://law.lis.virginia.gov/vacode/58.1-609.11/" target="_blank" rel="noreferrer">58.1-609.11</a>.</p><p>1989, c. 739; 1990, c. 349; 1993, c. 647; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0728" target="_blank" rel="noreferrer">728</a>; 1999, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0012" target="_blank" rel="noreferrer">12</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0334" target="_blank" rel="noreferrer">334</a>; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0493" target="_blank" rel="noreferrer">493</a>.</p>',7),n=[i];function l(s,p,h,d,c,f){return a(),t("div",null,n)}const m=e(r,[["render",l]]);export{g as __pageData,m as default};
