import{_ as t,o as a,c as r,z as e,a as o}from"./chunks/framework.9482e208.js";const x=JSON.parse('{"title":"§ 58.1-619 Returned goods.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-619.md","filePath":"vacode/58.1-619.md"}'),s={name:"vacode/58.1-619.md"},d=e("h1",{id:"§-58-1-619-returned-goods",tabindex:"-1"},[o("§ 58.1-619 Returned goods. "),e("a",{class:"header-anchor",href:"#§-58-1-619-returned-goods","aria-label":'Permalink to "§ 58.1-619 Returned goods."'},"​")],-1),n=e("p",null,"In the event purchases are returned to the dealer by the purchaser or consumer after the tax imposed by this chapter has been collected or charged to the account of the purchaser, the dealer shall be entitled to reimbursement of the amount of tax so collected or charged by him, in the manner prescribed by the Tax Commissioner. The amount of tax so reimbursed to the dealer shall not, however, include the tax paid upon any cash retained by the dealer after such return of merchandise. In case the tax has not been remitted by the dealer, the dealer may deduct the same in submitting his return. The dealer shall be issued an official credit memorandum by the Tax Commissioner equal to the net amount remitted by the dealer for such tax collected. Such memorandum shall be accepted at full face value from the dealer to whom it is issued when such dealer remits subsequent taxes under the provisions of this chapter. In case the dealer has retired from business and has filed a final return, a refund of tax may be made if the dealer can establish that the tax was not due.",-1),h=e("p",null,"Code 1950, § 58-441.22; 1966, c. 151; 1984, c. 675.",-1),c=[d,n,h];function i(l,u,m,f,p,b){return a(),r("div",null,c)}const y=t(s,[["render",i]]);export{x as __pageData,y as default};
