import{_ as e,o as i,c as r,O as t}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 58.1-641 Imposition of tire recycling fee.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-641.md","filePath":"vacode/58.1-641.md"}'),a={name:"vacode/58.1-641.md"},o=t('<h1 id="§-58-1-641-imposition-of-tire-recycling-fee" tabindex="-1">§ 58.1-641 Imposition of tire recycling fee. <a class="header-anchor" href="#§-58-1-641-imposition-of-tire-recycling-fee" aria-label="Permalink to &quot;§ 58.1-641 Imposition of tire recycling fee.&quot;">​</a></h1><p>Beginning July 1, 2008, there is hereby levied and imposed upon every retailer of tires in the Commonwealth, in addition to all other taxes and fees of every kind now imposed by law, a tire recycling fee of $1.00 for each new tire sold by a retailer. Beginning July 1, 2011, the fee shall be levied and imposed at a rate of $.50 for each new tire sold by a retailer.</p><p>1989, c. 630; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0101" target="_blank" rel="noreferrer">101</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0407" target="_blank" rel="noreferrer">407</a>; 2008, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0032" target="_blank" rel="noreferrer">32</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0158" target="_blank" rel="noreferrer">158</a>.</p>',3),n=[o];function l(c,s,f,_,d,p){return i(),r("div",null,n)}const m=e(a,[["render",l]]);export{h as __pageData,m as default};
