import{_ as t,o as a,c as o,z as e,a as s}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 58.1-660 Sale of business.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-660.md","filePath":"vacode/58.1-660.md"}'),n={name:"vacode/58.1-660.md"},r=e("h1",{id:"§-58-1-660-sale-of-business",tabindex:"-1"},[s("§ 58.1-660 Sale of business. "),e("a",{class:"header-anchor",href:"#§-58-1-660-sale-of-business","aria-label":'Permalink to "§ 58.1-660 Sale of business."'},"​")],-1),i=e("p",null,"If any communications services provider liable for any tax, penalty, or interest levied by this chapter sells his business or stock of goods or quits the business, he shall make a final return and payment within 15 days after the date of selling or quitting the business. His successors or assigns, if any, shall withhold a sufficient amount of the purchase money to cover taxes, penalties, and interest due and unpaid until the former owner produces a receipt from the Tax Commissioner showing that all taxes, penalties, and interest have been paid or a certificate stating that no taxes, penalties, or interest are due. If the purchaser of a business or stock of goods fails to withhold the purchase money as required above, he shall be personally liable for the payment of the taxes, penalties, and interest due and unpaid that were incurred by the business operation of the former owner. In no event, however, shall the tax, penalties, and interest due by the purchaser be more than the purchase price paid for the business or stock of goods.",-1),l=e("p",null,[s("2006, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0780",target:"_blank",rel:"noreferrer"},"780"),s(".")],-1),h=[r,i,l];function c(d,u,f,p,b,m){return a(),o("div",null,h)}const g=t(n,[["render",c]]);export{v as __pageData,g as default};
