import{_ as o,o as r,c as a,z as e,a as t}from"./chunks/framework.9482e208.js";const w=JSON.parse('{"title":"§ 58.1-804 Construction loan deeds of trust or mortgages.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-804.md","filePath":"vacode/58.1-804.md"}'),n={name:"vacode/58.1-804.md"},s=e("h1",{id:"§-58-1-804-construction-loan-deeds-of-trust-or-mortgages",tabindex:"-1"},[t("§ 58.1-804 Construction loan deeds of trust or mortgages. "),e("a",{class:"header-anchor",href:"#§-58-1-804-construction-loan-deeds-of-trust-or-mortgages","aria-label":'Permalink to "§ 58.1-804 Construction loan deeds of trust or mortgages."'},"​")],-1),d=e("p",null,'A. As used in this section, the term "construction loan deed of trust or mortgage" means a deed of trust or mortgage upon real estate, which states therein that it is given to secure a loan for real estate construction, and the terms of which provide that the principal sum owing under the instrument giving rise to the deed of trust or mortgage shall become due and payable on demand or three years or less from the date of such instrument. The term "permanent loan deed of trust or mortgage" means a deed of trust or mortgage upon real estate, the terms of which provide that the principal sum owing under the instrument giving rise to the deed of trust or mortgage shall become due and payable more than three years from the date of such instrument, and such deed of trust or mortgage secures an instrument made by the same persons who made the instrument which the construction loan deed of trust or mortgage secured and substantially the same real estate is conveyed thereby.',-1),i=e("p",null,[t("B. The tax provided by § "),e("a",{href:"http://law.lis.virginia.gov/vacode/58.1-803/",target:"_blank",rel:"noreferrer"},"58.1-803"),t(" shall apply to construction loan deeds of trust or mortgages.")],-1),h=e("p",null,[t("C. The tax provided by § "),e("a",{href:"http://law.lis.virginia.gov/vacode/58.1-803/",target:"_blank",rel:"noreferrer"},"58.1-803"),t(" shall not be imposed upon a permanent loan deed of trust or mortgage, as defined herein, if such deed of trust or mortgage is recorded within three years of the date of the recordation of the construction loan deed of trust or mortgage, as defined herein, and the tax on the construction loan deed of trust or mortgage has been paid. However, if the permanent loan deed of trust or mortgage, as defined herein, secures an instrument, the principal amount of which is more than the construction loan deed of trust or mortgage, the tax shall be imposed and calculated on the additional amount. Such permanent loan deed of trust or mortgage shall contain a reference to the construction loan deed of trust or mortgage and the book and page where recorded.")],-1),c=e("p",null,"Code 1950, § 58-55.1; 1970, c. 313; 1973, c. 139; 1977, c. 398; 1984, c. 675.",-1),l=[s,d,i,h,c];function u(m,g,f,p,_,v){return r(),a("div",null,l)}const y=o(n,[["render",u]]);export{w as __pageData,y as default};
