import{_ as e,o as t,c as r,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 58.1-809 When supplemental writings not taxable.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-809.md","filePath":"vacode/58.1-809.md"}'),o={name:"vacode/58.1-809.md"},n=a('<h1 id="§-58-1-809-when-supplemental-writings-not-taxable" tabindex="-1">§ 58.1-809 When supplemental writings not taxable. <a class="header-anchor" href="#§-58-1-809-when-supplemental-writings-not-taxable" aria-label="Permalink to &quot;§ 58.1-809 When supplemental writings not taxable.&quot;">​</a></h1><p>Sections <a href="http://law.lis.virginia.gov/vacode/58.1-803/" target="_blank" rel="noreferrer">58.1-803</a>, <a href="http://law.lis.virginia.gov/vacode/58.1-807/" target="_blank" rel="noreferrer">58.1-807</a>, and <a href="http://law.lis.virginia.gov/vacode/58.1-808/" target="_blank" rel="noreferrer">58.1-808</a> are not to be construed as requiring the payment of any tax for the recordation of any deed of trust, deed of subordination, mortgage, contract, agreement, modification, addendum, or other writing that is supplemental to any deed of trust, mortgage, contract, agreement, modification, addendum, or other writing theretofore admitted to record upon which the tax herein imposed has been paid, or which is exempt from the tax herein imposed by reason of subsection C of § <a href="http://law.lis.virginia.gov/vacode/58.1-804/" target="_blank" rel="noreferrer">58.1-804</a>, when the sole purpose and effect of the supplemental instrument is to wrap around a prior instrument, to convey property, in addition to or in substitution, in whole or in part, of the property conveyed in a prior instrument, to secure or to better secure the payment of the amount contracted for in a prior instrument, to alter the priority of a prior instrument, or to modify the terms, conditions, parties, or provisions of a prior instrument, other than to increase the amount of the principal obligation secured thereby.</p><p>The assumption of a deed of trust shall not be separately taxable under § <a href="http://law.lis.virginia.gov/vacode/58.1-801/" target="_blank" rel="noreferrer">58.1-801</a>, <a href="http://law.lis.virginia.gov/vacode/58.1-803/" target="_blank" rel="noreferrer">58.1-803</a> or <a href="http://law.lis.virginia.gov/vacode/58.1-807/" target="_blank" rel="noreferrer">58.1-807</a>, whether such assumption is by a separate instrument or included in the deed of conveyance.</p><p>Code 1950, § 58-60; 1977, c. 153; 1981, c. 621; 1984, c. 675; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0434" target="_blank" rel="noreferrer">434</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0488" target="_blank" rel="noreferrer">488</a>.</p>',4),i=[n];function s(l,p,h,d,c,f){return t(),r("div",null,i)}const m=e(o,[["render",s]]);export{u as __pageData,m as default};
