import{_ as t,o as n,c as a,z as e,a as o}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 58.1-910 Duty of resident representative of a nonresident decedent.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-910.md","filePath":"vacode/58.1-910.md"}'),r={name:"vacode/58.1-910.md"},s=e("h1",{id:"§-58-1-910-duty-of-resident-representative-of-a-nonresident-decedent",tabindex:"-1"},[o("§ 58.1-910 Duty of resident representative of a nonresident decedent. "),e("a",{class:"header-anchor",href:"#§-58-1-910-duty-of-resident-representative-of-a-nonresident-decedent","aria-label":'Permalink to "§ 58.1-910 Duty of resident representative of a nonresident decedent."'},"​")],-1),d=e("p",null,"A resident personal representative holding personal property of a deceased nonresident subject to the tax shall deduct the tax or collect it from the personal representative in the state of the decedent's domicile and shall not deliver such property to him or any other person until he has collected the tax and paid the same into the state treasury. When the transfer of such personal property is subject to a tax under the provisions of this chapter and the personal representative in the state of domicile neglects or refuses to pay the tax upon demand or if for any reason the tax is not paid within nine months after the decedent's death, the resident personal representative may, upon such notice as the circuit court of the county or city where such resident personal representative qualified may direct, be authorized to sell such property or, if the same can be divided, such portion as may be necessary. He shall then deduct the tax from the proceeds of such sale and account for the balance, if any, in lieu of the property.",-1),i=e("p",null,"Code 1950, § 58-238.11; 1978, c. 838; 1984, c. 675.",-1),c=[s,d,i];function h(p,l,f,u,m,y){return n(),a("div",null,c)}const x=t(r,[["render",h]]);export{v as __pageData,x as default};
