import{_ as o,o as t,c as r,z as e,a as s}from"./chunks/framework.9482e208.js";const g=JSON.parse(`{"title":"§ 59.1-115 Sheriff's sale of unbranded timber; recovery by owner; disposition of proceeds.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-115.md","filePath":"vacode/59.1-115.md"}`),a={name:"vacode/59.1-115.md"},i=e("h1",{id:"§-59-1-115-sheriff-s-sale-of-unbranded-timber-recovery-by-owner-disposition-of-proceeds",tabindex:"-1"},[s("§ 59.1-115 Sheriff's sale of unbranded timber; recovery by owner; disposition of proceeds. "),e("a",{class:"header-anchor",href:"#§-59-1-115-sheriff-s-sale-of-unbranded-timber-recovery-by-owner-disposition-of-proceeds","aria-label":`Permalink to "§ 59.1-115 Sheriff's sale of unbranded timber; recovery by owner; disposition of proceeds."`},"​")],-1),n=e("p",null,"Every person who shall take, catch, hold or have in his possession any log or other marketable timber, not branded as aforesaid, without the written consent of the owner thereof, shall within ten days after catching, taking up, or getting possession of the same, as aforesaid, report the same in writing to the county clerk of the county in which such person resides, and thirty days after such report is received the sheriff of such county shall sell the same publicly at the courthouse door on the first day of a circuit court in the county, of which notice shall be given by the sheriff for at least ten days by written or printed notices posted at the front door of such courthouse or near thereto and at one or more public places in the county. Any person owning such log or timber may, however, recover the same, by satisfying the sheriff that he is entitled to it, or by action of detinue, as provided by law. Such sale shall be made for cash, and the proceeds when collected, after paying the expenses of sale, including a fee of twenty-five cents for each log or piece of timber so sold, shall be paid to the treasurer of the county for the benefit of the public schools of the district in which the party reporting the same shall at that time reside. Any person failing to report to such clerk, as aforesaid, or to turn over the log or other timber to the sheriff, or any sheriff failing or refusing to advertise and sell such log or timber, as aforesaid, shall be guilty of a misdemeanor, and fined not less than $10 nor more than $100 for each offense.",-1),h=e("p",null,"Code 1950, § 59-212; 1968, c. 439.",-1),c=[i,n,h];function d(f,l,p,u,y,b){return t(),r("div",null,c)}const _=o(a,[["render",d]]);export{g as __pageData,_ as default};
