import{_ as e,o as i,c as t,O as n}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 59.1-162.1 Direct fueling of commercial vehicles authorized; conditions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-162.1.md","filePath":"vacode/59.1-162.1.md"}'),o={name:"vacode/59.1-162.1.md"},a=n('<h1 id="§-59-1-162-1-direct-fueling-of-commercial-vehicles-authorized-conditions" tabindex="-1">§ 59.1-162.1 Direct fueling of commercial vehicles authorized; conditions. <a class="header-anchor" href="#§-59-1-162-1-direct-fueling-of-commercial-vehicles-authorized-conditions" aria-label="Permalink to &quot;§ 59.1-162.1 Direct fueling of commercial vehicles authorized; conditions.&quot;">​</a></h1><p>Notwithstanding any other provision of law, the dispensing of diesel fuel from a tank vehicle into the fuel tank of any highway vehicle on the premises of a commercial, industrial, governmental or manufacturing establishment is permitted, provided the following conditions are met:</p><ol><li><p>The highway vehicle is used in connection with the business or function of the establishment;</p></li><li><p>The owner or operator of the tank vehicle complies with all requirements pertaining to the collection and payment of taxes on diesel fuel pursuant to Title 58.1 and fees pursuant to Title 62.1;</p></li><li><p>The owner or operator of the tank vehicle complies with all requirements pertaining to Chapter 56 (§ <a href="http://law.lis.virginia.gov/vacode/3.2-5600/" target="_blank" rel="noreferrer">3.2-5600</a> et seq.) of Title 3.2;</p></li><li><p>Each delivery shall be metered and recorded and the customer shall be provided an invoice or delivery ticket plainly indicating the quantity of fuel dispensed, the price per gallon, the amount of tax and the total price of the fuel dispensed;</p></li><li><p>The tank vehicle is designed, equipped and operated to prevent spills during fueling operations and to minimize spillage in the event of operator error or equipment malfunction;</p></li><li><p>The owner of the tank vehicle has established and maintains in place a contingency plan for the cleanup of spills occurring during fueling operations, and the operator has been trained in the prevention of spills, and containment of spills should they occur, and in compliance with such spill plan; and</p></li><li><p>The owner is licensed in Virginia as a distributor.</p></li></ol><p>2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0943" target="_blank" rel="noreferrer">943</a>.</p>',4),r=[a];function l(s,c,h,d,p,f){return i(),t("div",null,r)}const g=e(o,[["render",l]]);export{m as __pageData,g as default};
