import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 59.1-204.1 Tolling of limitation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-204.1.md","filePath":"vacode/59.1-204.1.md"}'),r={name:"vacode/59.1-204.1.md"},o=i('<h1 id="§-59-1-204-1-tolling-of-limitation" tabindex="-1">§ 59.1-204.1 Tolling of limitation. <a class="header-anchor" href="#§-59-1-204-1-tolling-of-limitation" aria-label="Permalink to &quot;§ 59.1-204.1 Tolling of limitation.&quot;">​</a></h1><p>A. Any individual action pursuant to § <a href="http://law.lis.virginia.gov/vacode/59.1-204/" target="_blank" rel="noreferrer">59.1-204</a> for which the right to bring such action first accrues on or after July 1, 1995, shall be commenced within two years after such accrual. The cause of action shall accrue as provided in § <a href="http://law.lis.virginia.gov/vacode/8.01-230/" target="_blank" rel="noreferrer">8.01-230</a>.</p><p>B. When any of the authorized government agencies files suit under this chapter, the time during which such governmental suit and all appeals therefrom is pending shall not be counted as any part of the period within which an action under § <a href="http://law.lis.virginia.gov/vacode/59.1-204/" target="_blank" rel="noreferrer">59.1-204</a> shall be brought.</p><p>1988, c. 241; 1995, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0703" target="_blank" rel="noreferrer">703</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0726" target="_blank" rel="noreferrer">726</a>.</p>',4),n=[o];function l(c,s,h,d,f,_){return a(),t("div",null,n)}const u=e(r,[["render",l]]);export{p as __pageData,u as default};
