import{_ as e,o as t,c as i,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 59.1-207.16 Action to be brought within certain time.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-207.16.md","filePath":"vacode/59.1-207.16.md"}'),r={name:"vacode/59.1-207.16.md"},a=o('<h1 id="§-59-1-207-16-action-to-be-brought-within-certain-time" tabindex="-1">§ 59.1-207.16 Action to be brought within certain time. <a class="header-anchor" href="#§-59-1-207-16-action-to-be-brought-within-certain-time" aria-label="Permalink to &quot;§ 59.1-207.16 Action to be brought within certain time.&quot;">​</a></h1><p>Any action brought under this chapter shall be commenced within the lemon law rights period. However, any consumer whose good faith attempts to settle the dispute pursuant to the informal dispute settlement provisions of § <a href="/vacode/59.1-207.15/">59.1-207.15</a> have not resulted in the satisfactory resolution of the matter shall have (i) 12 months from the date of the final action taken by the manufacturer in its dispute settlement procedure, if such procedure was resorted to within the lemon law rights period, or (ii) the original lemon law rights period, whichever is longer, to file an action in the proper court.</p><p>1988, c. 603; 1990, c. 772; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0411" target="_blank" rel="noreferrer">411</a>.</p>',3),n=[a];function c(h,s,l,d,p,_){return t(),i("div",null,n)}const f=e(r,[["render",c]]);export{m as __pageData,f as default};
