import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 59.1-207.38 Enforcement; penalties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-207.38.md","filePath":"vacode/59.1-207.38.md"}'),r={name:"vacode/59.1-207.38.md"},n=o('<h1 id="§-59-1-207-38-enforcement-penalties" tabindex="-1">§ 59.1-207.38 Enforcement; penalties. <a class="header-anchor" href="#§-59-1-207-38-enforcement-penalties" aria-label="Permalink to &quot;§ 59.1-207.38 Enforcement; penalties.&quot;">​</a></h1><p>Any violation of the provisions of this chapter shall constitute a prohibited practice pursuant to the provisions of § <a href="http://law.lis.virginia.gov/vacode/59.1-200/" target="_blank" rel="noreferrer">59.1-200</a> and shall be subject to any and all of the enforcement provisions of the Virginia Consumer Protection Act (§ <a href="http://law.lis.virginia.gov/vacode/59.1-196/" target="_blank" rel="noreferrer">59.1-196</a> et seq.) of this title. Notwithstanding any other provision to the contrary, it shall not be a violation of this chapter if the manufacturer within thirty days after the conclusion of the notice period required by subdivision 2 of § <a href="http://law.lis.virginia.gov/vacode/59.1-207.35/" target="_blank" rel="noreferrer">59.1-207.35</a>, upon notice from a consumer of the consumer&#39;s eligibility under an adjustment program, repairs or causes to be repaired the consumer&#39;s motor vehicle in accordance with the terms and conditions of the adjustment program.</p><p>1991, c. 300; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0063" target="_blank" rel="noreferrer">63</a>.</p>',3),i=[n];function s(c,l,h,p,f,d){return t(),a("div",null,i)}const u=e(r,[["render",s]]);export{m as __pageData,u as default};
